INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_18_verse_83', 'quran18verse83', 'Quran 18:83', 'quran-18-verse-83', '(Original Book)', 'ara', 'وَيَسْأَلُونَكَ عَنْ ذِي الْقَرْنَيْنِ ۖ قُلْ سَأَتْلُو عَلَيْكُمْ مِنْهُ ذِكْرًا', NULL, 82, 'quran_18');
