INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_18_verse_100', 'quran18verse100', 'Quran 18:100', 'quran-18-verse-100', '(Original Book)', 'ara', 'وَعَرَضْنَا جَهَنَّمَ يَوْمَئِذٍ لِلْكَافِرِينَ عَرْضًا', NULL, 99, 'quran_18');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_18_verse_101', 'quran18verse101', 'Quran 18:101', 'quran-18-verse-101', '(Original Book)', 'ara', 'الَّذِينَ كَانَتْ أَعْيُنُهُمْ فِي غِطَاءٍ عَنْ ذِكْرِي وَكَانُوا لَا يَسْتَطِيعُونَ سَمْعًا', NULL, 100, 'quran_18');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_18_verse_102', 'quran18verse102', 'Quran 18:102', 'quran-18-verse-102', '(Original Book)', 'ara', 'أَفَحَسِبَ الَّذِينَ كَفَرُوا أَنْ يَتَّخِذُوا عِبَادِي مِنْ دُونِي أَوْلِيَاءَ ۚ إِنَّا أَعْتَدْنَا جَهَنَّمَ لِلْكَافِرِينَ نُزُلًا', NULL, 101, 'quran_18');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_18_verse_103', 'quran18verse103', 'Quran 18:103', 'quran-18-verse-103', '(Original Book)', 'ara', 'قُلْ هَلْ نُنَبِّئُكُمْ بِالْأَخْسَرِينَ أَعْمَالًا', NULL, 102, 'quran_18');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_18_verse_104', 'quran18verse104', 'Quran 18:104', 'quran-18-verse-104', '(Original Book)', 'ara', 'الَّذِينَ ضَلَّ سَعْيُهُمْ فِي الْحَيَاةِ الدُّنْيَا وَهُمْ يَحْسَبُونَ أَنَّهُمْ يُحْسِنُونَ صُنْعًا', NULL, 103, 'quran_18');
