INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_20_verse_62', 'quran20verse62', 'Quran 20:62', 'quran-20-verse-62', '(Original Book)', 'ara', 'فَتَنَازَعُوا أَمْرَهُمْ بَيْنَهُمْ وَأَسَرُّوا النَّجْوَىٰ', NULL, 61, 'quran_20');
