INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_21_verse_70', 'quran21verse70', 'Quran 21:70', 'quran-21-verse-70', '(Original Book)', 'ara', 'وَأَرَادُوا بِهِ كَيْدًا فَجَعَلْنَاهُمُ الْأَخْسَرِينَ', NULL, 69, 'quran_21');
