INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_1', 'quran24verse1', 'Quran 24:1', 'quran-24-verse-1', '(Original Book)', 'ara', 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سُورَةٌ أَنْزَلْنَاهَا وَفَرَضْنَاهَا وَأَنْزَلْنَا فِيهَا آيَاتٍ بَيِّنَاتٍ لَعَلَّكُمْ تَذَكَّرُونَ', NULL, 0, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_2', 'quran24verse2', 'Quran 24:2', 'quran-24-verse-2', '(Original Book)', 'ara', 'الزَّانِيَةُ وَالزَّانِي فَاجْلِدُوا كُلَّ وَاحِدٍ مِنْهُمَا مِائَةَ جَلْدَةٍ ۖ وَلَا تَأْخُذْكُمْ بِهِمَا رَأْفَةٌ فِي دِينِ اللَّهِ إِنْ كُنْتُمْ تُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۖ وَلْيَشْهَدْ عَذَابَهُمَا طَائِفَةٌ مِنَ الْمُؤْمِنِينَ', NULL, 1, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_3', 'quran24verse3', 'Quran 24:3', 'quran-24-verse-3', '(Original Book)', 'ara', 'الزَّانِي لَا يَنْكِحُ إِلَّا زَانِيَةً أَوْ مُشْرِكَةً وَالزَّانِيَةُ لَا يَنْكِحُهَا إِلَّا زَانٍ أَوْ مُشْرِكٌ ۚ وَحُرِّمَ ذَٰلِكَ عَلَى الْمُؤْمِنِينَ', NULL, 2, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_4', 'quran24verse4', 'Quran 24:4', 'quran-24-verse-4', '(Original Book)', 'ara', 'وَالَّذِينَ يَرْمُونَ الْمُحْصَنَاتِ ثُمَّ لَمْ يَأْتُوا بِأَرْبَعَةِ شُهَدَاءَ فَاجْلِدُوهُمْ ثَمَانِينَ جَلْدَةً وَلَا تَقْبَلُوا لَهُمْ شَهَادَةً أَبَدًا ۚ وَأُولَٰئِكَ هُمُ الْفَاسِقُونَ', NULL, 3, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_5', 'quran24verse5', 'Quran 24:5', 'quran-24-verse-5', '(Original Book)', 'ara', 'إِلَّا الَّذِينَ تَابُوا مِنْ بَعْدِ ذَٰلِكَ وَأَصْلَحُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', NULL, 4, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_6', 'quran24verse6', 'Quran 24:6', 'quran-24-verse-6', '(Original Book)', 'ara', 'وَالَّذِينَ يَرْمُونَ أَزْوَاجَهُمْ وَلَمْ يَكُنْ لَهُمْ شُهَدَاءُ إِلَّا أَنْفُسُهُمْ فَشَهَادَةُ أَحَدِهِمْ أَرْبَعُ شَهَادَاتٍ بِاللَّهِ ۙ إِنَّهُ لَمِنَ الصَّادِقِينَ', NULL, 5, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_7', 'quran24verse7', 'Quran 24:7', 'quran-24-verse-7', '(Original Book)', 'ara', 'وَالْخَامِسَةُ أَنَّ لَعْنَتَ اللَّهِ عَلَيْهِ إِنْ كَانَ مِنَ الْكَاذِبِينَ', NULL, 6, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_8', 'quran24verse8', 'Quran 24:8', 'quran-24-verse-8', '(Original Book)', 'ara', 'وَيَدْرَأُ عَنْهَا الْعَذَابَ أَنْ تَشْهَدَ أَرْبَعَ شَهَادَاتٍ بِاللَّهِ ۙ إِنَّهُ لَمِنَ الْكَاذِبِينَ', NULL, 7, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_9', 'quran24verse9', 'Quran 24:9', 'quran-24-verse-9', '(Original Book)', 'ara', 'وَالْخَامِسَةَ أَنَّ غَضَبَ اللَّهِ عَلَيْهَا إِنْ كَانَ مِنَ الصَّادِقِينَ', NULL, 8, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_10', 'quran24verse10', 'Quran 24:10', 'quran-24-verse-10', '(Original Book)', 'ara', 'وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ وَأَنَّ اللَّهَ تَوَّابٌ حَكِيمٌ', NULL, 9, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_11', 'quran24verse11', 'Quran 24:11', 'quran-24-verse-11', '(Original Book)', 'ara', 'إِنَّ الَّذِينَ جَاءُوا بِالْإِفْكِ عُصْبَةٌ مِنْكُمْ ۚ لَا تَحْسَبُوهُ شَرًّا لَكُمْ ۖ بَلْ هُوَ خَيْرٌ لَكُمْ ۚ لِكُلِّ امْرِئٍ مِنْهُمْ مَا اكْتَسَبَ مِنَ الْإِثْمِ ۚ وَالَّذِي تَوَلَّىٰ كِبْرَهُ مِنْهُمْ لَهُ عَذَابٌ عَظِيمٌ', NULL, 10, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_12', 'quran24verse12', 'Quran 24:12', 'quran-24-verse-12', '(Original Book)', 'ara', 'لَوْلَا إِذْ سَمِعْتُمُوهُ ظَنَّ الْمُؤْمِنُونَ وَالْمُؤْمِنَاتُ بِأَنْفُسِهِمْ خَيْرًا وَقَالُوا هَٰذَا إِفْكٌ مُبِينٌ', NULL, 11, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_13', 'quran24verse13', 'Quran 24:13', 'quran-24-verse-13', '(Original Book)', 'ara', 'لَوْلَا جَاءُوا عَلَيْهِ بِأَرْبَعَةِ شُهَدَاءَ ۚ فَإِذْ لَمْ يَأْتُوا بِالشُّهَدَاءِ فَأُولَٰئِكَ عِنْدَ اللَّهِ هُمُ الْكَاذِبُونَ', NULL, 12, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_14', 'quran24verse14', 'Quran 24:14', 'quran-24-verse-14', '(Original Book)', 'ara', 'وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ فِي الدُّنْيَا وَالْآخِرَةِ لَمَسَّكُمْ فِي مَا أَفَضْتُمْ فِيهِ عَذَابٌ عَظِيمٌ', NULL, 13, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_15', 'quran24verse15', 'Quran 24:15', 'quran-24-verse-15', '(Original Book)', 'ara', 'إِذْ تَلَقَّوْنَهُ بِأَلْسِنَتِكُمْ وَتَقُولُونَ بِأَفْوَاهِكُمْ مَا لَيْسَ لَكُمْ بِهِ عِلْمٌ وَتَحْسَبُونَهُ هَيِّنًا وَهُوَ عِنْدَ اللَّهِ عَظِيمٌ', NULL, 14, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_16', 'quran24verse16', 'Quran 24:16', 'quran-24-verse-16', '(Original Book)', 'ara', 'وَلَوْلَا إِذْ سَمِعْتُمُوهُ قُلْتُمْ مَا يَكُونُ لَنَا أَنْ نَتَكَلَّمَ بِهَٰذَا سُبْحَانَكَ هَٰذَا بُهْتَانٌ عَظِيمٌ', NULL, 15, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_17', 'quran24verse17', 'Quran 24:17', 'quran-24-verse-17', '(Original Book)', 'ara', 'يَعِظُكُمُ اللَّهُ أَنْ تَعُودُوا لِمِثْلِهِ أَبَدًا إِنْ كُنْتُمْ مُؤْمِنِينَ', NULL, 16, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_18', 'quran24verse18', 'Quran 24:18', 'quran-24-verse-18', '(Original Book)', 'ara', 'وَيُبَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ ۚ وَاللَّهُ عَلِيمٌ حَكِيمٌ', NULL, 17, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_19', 'quran24verse19', 'Quran 24:19', 'quran-24-verse-19', '(Original Book)', 'ara', 'إِنَّ الَّذِينَ يُحِبُّونَ أَنْ تَشِيعَ الْفَاحِشَةُ فِي الَّذِينَ آمَنُوا لَهُمْ عَذَابٌ أَلِيمٌ فِي الدُّنْيَا وَالْآخِرَةِ ۚ وَاللَّهُ يَعْلَمُ وَأَنْتُمْ لَا تَعْلَمُونَ', NULL, 18, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_20', 'quran24verse20', 'Quran 24:20', 'quran-24-verse-20', '(Original Book)', 'ara', 'وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ وَأَنَّ اللَّهَ رَءُوفٌ رَحِيمٌ', NULL, 19, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_21', 'quran24verse21', 'Quran 24:21', 'quran-24-verse-21', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّبِعُوا خُطُوَاتِ الشَّيْطَانِ ۚ وَمَنْ يَتَّبِعْ خُطُوَاتِ الشَّيْطَانِ فَإِنَّهُ يَأْمُرُ بِالْفَحْشَاءِ وَالْمُنْكَرِ ۚ وَلَوْلَا فَضْلُ اللَّهِ عَلَيْكُمْ وَرَحْمَتُهُ مَا زَكَىٰ مِنْكُمْ مِنْ أَحَدٍ أَبَدًا وَلَٰكِنَّ اللَّهَ يُزَكِّي مَنْ يَشَاءُ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ', NULL, 20, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_22', 'quran24verse22', 'Quran 24:22', 'quran-24-verse-22', '(Original Book)', 'ara', 'وَلَا يَأْتَلِ أُولُو الْفَضْلِ مِنْكُمْ وَالسَّعَةِ أَنْ يُؤْتُوا أُولِي الْقُرْبَىٰ وَالْمَسَاكِينَ وَالْمُهَاجِرِينَ فِي سَبِيلِ اللَّهِ ۖ وَلْيَعْفُوا وَلْيَصْفَحُوا ۗ أَلَا تُحِبُّونَ أَنْ يَغْفِرَ اللَّهُ لَكُمْ ۗ وَاللَّهُ غَفُورٌ رَحِيمٌ', NULL, 21, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_23', 'quran24verse23', 'Quran 24:23', 'quran-24-verse-23', '(Original Book)', 'ara', 'إِنَّ الَّذِينَ يَرْمُونَ الْمُحْصَنَاتِ الْغَافِلَاتِ الْمُؤْمِنَاتِ لُعِنُوا فِي الدُّنْيَا وَالْآخِرَةِ وَلَهُمْ عَذَابٌ عَظِيمٌ', NULL, 22, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_24', 'quran24verse24', 'Quran 24:24', 'quran-24-verse-24', '(Original Book)', 'ara', 'يَوْمَ تَشْهَدُ عَلَيْهِمْ أَلْسِنَتُهُمْ وَأَيْدِيهِمْ وَأَرْجُلُهُمْ بِمَا كَانُوا يَعْمَلُونَ', NULL, 23, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_25', 'quran24verse25', 'Quran 24:25', 'quran-24-verse-25', '(Original Book)', 'ara', 'يَوْمَئِذٍ يُوَفِّيهِمُ اللَّهُ دِينَهُمُ الْحَقَّ وَيَعْلَمُونَ أَنَّ اللَّهَ هُوَ الْحَقُّ الْمُبِينُ', NULL, 24, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_26', 'quran24verse26', 'Quran 24:26', 'quran-24-verse-26', '(Original Book)', 'ara', 'الْخَبِيثَاتُ لِلْخَبِيثِينَ وَالْخَبِيثُونَ لِلْخَبِيثَاتِ ۖ وَالطَّيِّبَاتُ لِلطَّيِّبِينَ وَالطَّيِّبُونَ لِلطَّيِّبَاتِ ۚ أُولَٰئِكَ مُبَرَّءُونَ مِمَّا يَقُولُونَ ۖ لَهُمْ مَغْفِرَةٌ وَرِزْقٌ كَرِيمٌ', NULL, 25, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_27', 'quran24verse27', 'Quran 24:27', 'quran-24-verse-27', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَدْخُلُوا بُيُوتًا غَيْرَ بُيُوتِكُمْ حَتَّىٰ تَسْتَأْنِسُوا وَتُسَلِّمُوا عَلَىٰ أَهْلِهَا ۚ ذَٰلِكُمْ خَيْرٌ لَكُمْ لَعَلَّكُمْ تَذَكَّرُونَ', NULL, 26, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_26_verse_6', 'quran26verse6', 'Quran 26:6', 'quran-26-verse-6', '(Original Book)', 'ara', 'فَقَدْ كَذَّبُوا فَسَيَأْتِيهِمْ أَنْبَاءُ مَا كَانُوا بِهِ يَسْتَهْزِئُونَ', NULL, 5, 'quran_26');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_28', 'quran24verse28', 'Quran 24:28', 'quran-24-verse-28', '(Original Book)', 'ara', 'فَإِنْ لَمْ تَجِدُوا فِيهَا أَحَدًا فَلَا تَدْخُلُوهَا حَتَّىٰ يُؤْذَنَ لَكُمْ ۖ وَإِنْ قِيلَ لَكُمُ ارْجِعُوا فَارْجِعُوا ۖ هُوَ أَزْكَىٰ لَكُمْ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ عَلِيمٌ', NULL, 27, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_29', 'quran24verse29', 'Quran 24:29', 'quran-24-verse-29', '(Original Book)', 'ara', 'لَيْسَ عَلَيْكُمْ جُنَاحٌ أَنْ تَدْخُلُوا بُيُوتًا غَيْرَ مَسْكُونَةٍ فِيهَا مَتَاعٌ لَكُمْ ۚ وَاللَّهُ يَعْلَمُ مَا تُبْدُونَ وَمَا تَكْتُمُونَ', NULL, 28, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_30', 'quran24verse30', 'Quran 24:30', 'quran-24-verse-30', '(Original Book)', 'ara', 'قُلْ لِلْمُؤْمِنِينَ يَغُضُّوا مِنْ أَبْصَارِهِمْ وَيَحْفَظُوا فُرُوجَهُمْ ۚ ذَٰلِكَ أَزْكَىٰ لَهُمْ ۗ إِنَّ اللَّهَ خَبِيرٌ بِمَا يَصْنَعُونَ', NULL, 29, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_31', 'quran24verse31', 'Quran 24:31', 'quran-24-verse-31', '(Original Book)', 'ara', 'وَقُلْ لِلْمُؤْمِنَاتِ يَغْضُضْنَ مِنْ أَبْصَارِهِنَّ وَيَحْفَظْنَ فُرُوجَهُنَّ وَلَا يُبْدِينَ زِينَتَهُنَّ إِلَّا مَا ظَهَرَ مِنْهَا ۖ وَلْيَضْرِبْنَ بِخُمُرِهِنَّ عَلَىٰ جُيُوبِهِنَّ ۖ وَلَا يُبْدِينَ زِينَتَهُنَّ إِلَّا لِبُعُولَتِهِنَّ أَوْ آبَائِهِنَّ أَوْ آبَاءِ بُعُولَتِهِنَّ أَوْ أَبْنَائِهِنَّ أَوْ أَبْنَاءِ بُعُولَتِهِنَّ أَوْ إِخْوَانِهِنَّ أَوْ بَنِي إِخْوَانِهِنَّ أَوْ بَنِي أَخَوَاتِهِنَّ أَوْ نِسَائِهِنَّ أَوْ مَا مَلَكَتْ أَيْمَانُهُنَّ أَوِ التَّابِعِينَ غَيْرِ أُولِي الْإِرْبَةِ مِنَ الرِّجَالِ أَوِ الطِّفْلِ الَّذِينَ لَمْ يَظْهَرُوا عَلَىٰ عَوْرَاتِ النِّسَاءِ ۖ وَلَا يَضْرِبْنَ بِأَرْجُلِهِنَّ لِيُعْلَمَ مَا يُخْفِينَ مِنْ زِينَتِهِنَّ ۚ وَتُوبُوا إِلَى اللَّهِ جَمِيعًا أَيُّهَ الْمُؤْمِنُونَ لَعَلَّكُمْ تُفْلِحُونَ', NULL, 30, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_32', 'quran24verse32', 'Quran 24:32', 'quran-24-verse-32', '(Original Book)', 'ara', 'وَأَنْكِحُوا الْأَيَامَىٰ مِنْكُمْ وَالصَّالِحِينَ مِنْ عِبَادِكُمْ وَإِمَائِكُمْ ۚ إِنْ يَكُونُوا فُقَرَاءَ يُغْنِهِمُ اللَّهُ مِنْ فَضْلِهِ ۗ وَاللَّهُ وَاسِعٌ عَلِيمٌ', NULL, 31, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_33', 'quran24verse33', 'Quran 24:33', 'quran-24-verse-33', '(Original Book)', 'ara', 'وَلْيَسْتَعْفِفِ الَّذِينَ لَا يَجِدُونَ نِكَاحًا حَتَّىٰ يُغْنِيَهُمُ اللَّهُ مِنْ فَضْلِهِ ۗ وَالَّذِينَ يَبْتَغُونَ الْكِتَابَ مِمَّا مَلَكَتْ أَيْمَانُكُمْ فَكَاتِبُوهُمْ إِنْ عَلِمْتُمْ فِيهِمْ خَيْرًا ۖ وَآتُوهُمْ مِنْ مَالِ اللَّهِ الَّذِي آتَاكُمْ ۚ وَلَا تُكْرِهُوا فَتَيَاتِكُمْ عَلَى الْبِغَاءِ إِنْ أَرَدْنَ تَحَصُّنًا لِتَبْتَغُوا عَرَضَ الْحَيَاةِ الدُّنْيَا ۚ وَمَنْ يُكْرِهْهُنَّ فَإِنَّ اللَّهَ مِنْ بَعْدِ إِكْرَاهِهِنَّ غَفُورٌ رَحِيمٌ', NULL, 32, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_34', 'quran24verse34', 'Quran 24:34', 'quran-24-verse-34', '(Original Book)', 'ara', 'وَلَقَدْ أَنْزَلْنَا إِلَيْكُمْ آيَاتٍ مُبَيِّنَاتٍ وَمَثَلًا مِنَ الَّذِينَ خَلَوْا مِنْ قَبْلِكُمْ وَمَوْعِظَةً لِلْمُتَّقِينَ', NULL, 33, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_35', 'quran24verse35', 'Quran 24:35', 'quran-24-verse-35', '(Original Book)', 'ara', 'اللَّهُ نُورُ السَّمَاوَاتِ وَالْأَرْضِ ۚ مَثَلُ نُورِهِ كَمِشْكَاةٍ فِيهَا مِصْبَاحٌ ۖ الْمِصْبَاحُ فِي زُجَاجَةٍ ۖ الزُّجَاجَةُ كَأَنَّهَا كَوْكَبٌ دُرِّيٌّ يُوقَدُ مِنْ شَجَرَةٍ مُبَارَكَةٍ زَيْتُونَةٍ لَا شَرْقِيَّةٍ وَلَا غَرْبِيَّةٍ يَكَادُ زَيْتُهَا يُضِيءُ وَلَوْ لَمْ تَمْسَسْهُ نَارٌ ۚ نُورٌ عَلَىٰ نُورٍ ۗ يَهْدِي اللَّهُ لِنُورِهِ مَنْ يَشَاءُ ۚ وَيَضْرِبُ اللَّهُ الْأَمْثَالَ لِلنَّاسِ ۗ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ', NULL, 34, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_36', 'quran24verse36', 'Quran 24:36', 'quran-24-verse-36', '(Original Book)', 'ara', 'فِي بُيُوتٍ أَذِنَ اللَّهُ أَنْ تُرْفَعَ وَيُذْكَرَ فِيهَا اسْمُهُ يُسَبِّحُ لَهُ فِيهَا بِالْغُدُوِّ وَالْآصَالِ', NULL, 35, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_37', 'quran24verse37', 'Quran 24:37', 'quran-24-verse-37', '(Original Book)', 'ara', 'رِجَالٌ لَا تُلْهِيهِمْ تِجَارَةٌ وَلَا بَيْعٌ عَنْ ذِكْرِ اللَّهِ وَإِقَامِ الصَّلَاةِ وَإِيتَاءِ الزَّكَاةِ ۙ يَخَافُونَ يَوْمًا تَتَقَلَّبُ فِيهِ الْقُلُوبُ وَالْأَبْصَارُ', NULL, 36, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_38', 'quran24verse38', 'Quran 24:38', 'quran-24-verse-38', '(Original Book)', 'ara', 'لِيَجْزِيَهُمُ اللَّهُ أَحْسَنَ مَا عَمِلُوا وَيَزِيدَهُمْ مِنْ فَضْلِهِ ۗ وَاللَّهُ يَرْزُقُ مَنْ يَشَاءُ بِغَيْرِ حِسَابٍ', NULL, 37, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_39', 'quran24verse39', 'Quran 24:39', 'quran-24-verse-39', '(Original Book)', 'ara', 'وَالَّذِينَ كَفَرُوا أَعْمَالُهُمْ كَسَرَابٍ بِقِيعَةٍ يَحْسَبُهُ الظَّمْآنُ مَاءً حَتَّىٰ إِذَا جَاءَهُ لَمْ يَجِدْهُ شَيْئًا وَوَجَدَ اللَّهَ عِنْدَهُ فَوَفَّاهُ حِسَابَهُ ۗ وَاللَّهُ سَرِيعُ الْحِسَابِ', NULL, 38, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_40', 'quran24verse40', 'Quran 24:40', 'quran-24-verse-40', '(Original Book)', 'ara', 'أَوْ كَظُلُمَاتٍ فِي بَحْرٍ لُجِّيٍّ يَغْشَاهُ مَوْجٌ مِنْ فَوْقِهِ مَوْجٌ مِنْ فَوْقِهِ سَحَابٌ ۚ ظُلُمَاتٌ بَعْضُهَا فَوْقَ بَعْضٍ إِذَا أَخْرَجَ يَدَهُ لَمْ يَكَدْ يَرَاهَا ۗ وَمَنْ لَمْ يَجْعَلِ اللَّهُ لَهُ نُورًا فَمَا لَهُ مِنْ نُورٍ', NULL, 39, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_41', 'quran24verse41', 'Quran 24:41', 'quran-24-verse-41', '(Original Book)', 'ara', 'أَلَمْ تَرَ أَنَّ اللَّهَ يُسَبِّحُ لَهُ مَنْ فِي السَّمَاوَاتِ وَالْأَرْضِ وَالطَّيْرُ صَافَّاتٍ ۖ كُلٌّ قَدْ عَلِمَ صَلَاتَهُ وَتَسْبِيحَهُ ۗ وَاللَّهُ عَلِيمٌ بِمَا يَفْعَلُونَ', NULL, 40, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_42', 'quran24verse42', 'Quran 24:42', 'quran-24-verse-42', '(Original Book)', 'ara', 'وَلِلَّهِ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۖ وَإِلَى اللَّهِ الْمَصِيرُ', NULL, 41, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_43', 'quran24verse43', 'Quran 24:43', 'quran-24-verse-43', '(Original Book)', 'ara', 'أَلَمْ تَرَ أَنَّ اللَّهَ يُزْجِي سَحَابًا ثُمَّ يُؤَلِّفُ بَيْنَهُ ثُمَّ يَجْعَلُهُ رُكَامًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلَالِهِ وَيُنَزِّلُ مِنَ السَّمَاءِ مِنْ جِبَالٍ فِيهَا مِنْ بَرَدٍ فَيُصِيبُ بِهِ مَنْ يَشَاءُ وَيَصْرِفُهُ عَنْ مَنْ يَشَاءُ ۖ يَكَادُ سَنَا بَرْقِهِ يَذْهَبُ بِالْأَبْصَارِ', NULL, 42, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_44', 'quran24verse44', 'Quran 24:44', 'quran-24-verse-44', '(Original Book)', 'ara', 'يُقَلِّبُ اللَّهُ اللَّيْلَ وَالنَّهَارَ ۚ إِنَّ فِي ذَٰلِكَ لَعِبْرَةً لِأُولِي الْأَبْصَارِ', NULL, 43, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_45', 'quran24verse45', 'Quran 24:45', 'quran-24-verse-45', '(Original Book)', 'ara', 'وَاللَّهُ خَلَقَ كُلَّ دَابَّةٍ مِنْ مَاءٍ ۖ فَمِنْهُمْ مَنْ يَمْشِي عَلَىٰ بَطْنِهِ وَمِنْهُمْ مَنْ يَمْشِي عَلَىٰ رِجْلَيْنِ وَمِنْهُمْ مَنْ يَمْشِي عَلَىٰ أَرْبَعٍ ۚ يَخْلُقُ اللَّهُ مَا يَشَاءُ ۚ إِنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', NULL, 44, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_46', 'quran24verse46', 'Quran 24:46', 'quran-24-verse-46', '(Original Book)', 'ara', 'لَقَدْ أَنْزَلْنَا آيَاتٍ مُبَيِّنَاتٍ ۚ وَاللَّهُ يَهْدِي مَنْ يَشَاءُ إِلَىٰ صِرَاطٍ مُسْتَقِيمٍ', NULL, 45, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_47', 'quran24verse47', 'Quran 24:47', 'quran-24-verse-47', '(Original Book)', 'ara', 'وَيَقُولُونَ آمَنَّا بِاللَّهِ وَبِالرَّسُولِ وَأَطَعْنَا ثُمَّ يَتَوَلَّىٰ فَرِيقٌ مِنْهُمْ مِنْ بَعْدِ ذَٰلِكَ ۚ وَمَا أُولَٰئِكَ بِالْمُؤْمِنِينَ', NULL, 46, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_48', 'quran24verse48', 'Quran 24:48', 'quran-24-verse-48', '(Original Book)', 'ara', 'وَإِذَا دُعُوا إِلَى اللَّهِ وَرَسُولِهِ لِيَحْكُمَ بَيْنَهُمْ إِذَا فَرِيقٌ مِنْهُمْ مُعْرِضُونَ', NULL, 47, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_49', 'quran24verse49', 'Quran 24:49', 'quran-24-verse-49', '(Original Book)', 'ara', 'وَإِنْ يَكُنْ لَهُمُ الْحَقُّ يَأْتُوا إِلَيْهِ مُذْعِنِينَ', NULL, 48, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_50', 'quran24verse50', 'Quran 24:50', 'quran-24-verse-50', '(Original Book)', 'ara', 'أَفِي قُلُوبِهِمْ مَرَضٌ أَمِ ارْتَابُوا أَمْ يَخَافُونَ أَنْ يَحِيفَ اللَّهُ عَلَيْهِمْ وَرَسُولُهُ ۚ بَلْ أُولَٰئِكَ هُمُ الظَّالِمُونَ', NULL, 49, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_51', 'quran24verse51', 'Quran 24:51', 'quran-24-verse-51', '(Original Book)', 'ara', 'إِنَّمَا كَانَ قَوْلَ الْمُؤْمِنِينَ إِذَا دُعُوا إِلَى اللَّهِ وَرَسُولِهِ لِيَحْكُمَ بَيْنَهُمْ أَنْ يَقُولُوا سَمِعْنَا وَأَطَعْنَا ۚ وَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ', NULL, 50, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_52', 'quran24verse52', 'Quran 24:52', 'quran-24-verse-52', '(Original Book)', 'ara', 'وَمَنْ يُطِعِ اللَّهَ وَرَسُولَهُ وَيَخْشَ اللَّهَ وَيَتَّقْهِ فَأُولَٰئِكَ هُمُ الْفَائِزُونَ', NULL, 51, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_53', 'quran24verse53', 'Quran 24:53', 'quran-24-verse-53', '(Original Book)', 'ara', 'وَأَقْسَمُوا بِاللَّهِ جَهْدَ أَيْمَانِهِمْ لَئِنْ أَمَرْتَهُمْ لَيَخْرُجُنَّ ۖ قُلْ لَا تُقْسِمُوا ۖ طَاعَةٌ مَعْرُوفَةٌ ۚ إِنَّ اللَّهَ خَبِيرٌ بِمَا تَعْمَلُونَ', NULL, 52, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_54', 'quran24verse54', 'Quran 24:54', 'quran-24-verse-54', '(Original Book)', 'ara', 'قُلْ أَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ ۖ فَإِنْ تَوَلَّوْا فَإِنَّمَا عَلَيْهِ مَا حُمِّلَ وَعَلَيْكُمْ مَا حُمِّلْتُمْ ۖ وَإِنْ تُطِيعُوهُ تَهْتَدُوا ۚ وَمَا عَلَى الرَّسُولِ إِلَّا الْبَلَاغُ الْمُبِينُ', NULL, 53, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_55', 'quran24verse55', 'Quran 24:55', 'quran-24-verse-55', '(Original Book)', 'ara', 'وَعَدَ اللَّهُ الَّذِينَ آمَنُوا مِنْكُمْ وَعَمِلُوا الصَّالِحَاتِ لَيَسْتَخْلِفَنَّهُمْ فِي الْأَرْضِ كَمَا اسْتَخْلَفَ الَّذِينَ مِنْ قَبْلِهِمْ وَلَيُمَكِّنَنَّ لَهُمْ دِينَهُمُ الَّذِي ارْتَضَىٰ لَهُمْ وَلَيُبَدِّلَنَّهُمْ مِنْ بَعْدِ خَوْفِهِمْ أَمْنًا ۚ يَعْبُدُونَنِي لَا يُشْرِكُونَ بِي شَيْئًا ۚ وَمَنْ كَفَرَ بَعْدَ ذَٰلِكَ فَأُولَٰئِكَ هُمُ الْفَاسِقُونَ', NULL, 54, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_56', 'quran24verse56', 'Quran 24:56', 'quran-24-verse-56', '(Original Book)', 'ara', 'وَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَأَطِيعُوا الرَّسُولَ لَعَلَّكُمْ تُرْحَمُونَ', NULL, 55, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_57', 'quran24verse57', 'Quran 24:57', 'quran-24-verse-57', '(Original Book)', 'ara', 'لَا تَحْسَبَنَّ الَّذِينَ كَفَرُوا مُعْجِزِينَ فِي الْأَرْضِ ۚ وَمَأْوَاهُمُ النَّارُ ۖ وَلَبِئْسَ الْمَصِيرُ', NULL, 56, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_7', 'quran25verse7', 'Quran 25:7', 'quran-25-verse-7', '(Original Book)', 'ara', 'وَقَالُوا مَالِ هَٰذَا الرَّسُولِ يَأْكُلُ الطَّعَامَ وَيَمْشِي فِي الْأَسْوَاقِ ۙ لَوْلَا أُنْزِلَ إِلَيْهِ مَلَكٌ فَيَكُونَ مَعَهُ نَذِيرًا', NULL, 6, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_57', 'quran25verse57', 'Quran 25:57', 'quran-25-verse-57', '(Original Book)', 'ara', 'قُلْ مَا أَسْأَلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِلَّا مَنْ شَاءَ أَنْ يَتَّخِذَ إِلَىٰ رَبِّهِ سَبِيلًا', NULL, 56, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_58', 'quran24verse58', 'Quran 24:58', 'quran-24-verse-58', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا لِيَسْتَأْذِنْكُمُ الَّذِينَ مَلَكَتْ أَيْمَانُكُمْ وَالَّذِينَ لَمْ يَبْلُغُوا الْحُلُمَ مِنْكُمْ ثَلَاثَ مَرَّاتٍ ۚ مِنْ قَبْلِ صَلَاةِ الْفَجْرِ وَحِينَ تَضَعُونَ ثِيَابَكُمْ مِنَ الظَّهِيرَةِ وَمِنْ بَعْدِ صَلَاةِ الْعِشَاءِ ۚ ثَلَاثُ عَوْرَاتٍ لَكُمْ ۚ لَيْسَ عَلَيْكُمْ وَلَا عَلَيْهِمْ جُنَاحٌ بَعْدَهُنَّ ۚ طَوَّافُونَ عَلَيْكُمْ بَعْضُكُمْ عَلَىٰ بَعْضٍ ۚ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ', NULL, 57, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_59', 'quran24verse59', 'Quran 24:59', 'quran-24-verse-59', '(Original Book)', 'ara', 'وَإِذَا بَلَغَ الْأَطْفَالُ مِنْكُمُ الْحُلُمَ فَلْيَسْتَأْذِنُوا كَمَا اسْتَأْذَنَ الَّذِينَ مِنْ قَبْلِهِمْ ۚ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمْ آيَاتِهِ ۗ وَاللَّهُ عَلِيمٌ حَكِيمٌ', NULL, 58, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_60', 'quran24verse60', 'Quran 24:60', 'quran-24-verse-60', '(Original Book)', 'ara', 'وَالْقَوَاعِدُ مِنَ النِّسَاءِ اللَّاتِي لَا يَرْجُونَ نِكَاحًا فَلَيْسَ عَلَيْهِنَّ جُنَاحٌ أَنْ يَضَعْنَ ثِيَابَهُنَّ غَيْرَ مُتَبَرِّجَاتٍ بِزِينَةٍ ۖ وَأَنْ يَسْتَعْفِفْنَ خَيْرٌ لَهُنَّ ۗ وَاللَّهُ سَمِيعٌ عَلِيمٌ', NULL, 59, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_61', 'quran24verse61', 'Quran 24:61', 'quran-24-verse-61', '(Original Book)', 'ara', 'لَيْسَ عَلَى الْأَعْمَىٰ حَرَجٌ وَلَا عَلَى الْأَعْرَجِ حَرَجٌ وَلَا عَلَى الْمَرِيضِ حَرَجٌ وَلَا عَلَىٰ أَنْفُسِكُمْ أَنْ تَأْكُلُوا مِنْ بُيُوتِكُمْ أَوْ بُيُوتِ آبَائِكُمْ أَوْ بُيُوتِ أُمَّهَاتِكُمْ أَوْ بُيُوتِ إِخْوَانِكُمْ أَوْ بُيُوتِ أَخَوَاتِكُمْ أَوْ بُيُوتِ أَعْمَامِكُمْ أَوْ بُيُوتِ عَمَّاتِكُمْ أَوْ بُيُوتِ أَخْوَالِكُمْ أَوْ بُيُوتِ خَالَاتِكُمْ أَوْ مَا مَلَكْتُمْ مَفَاتِحَهُ أَوْ صَدِيقِكُمْ ۚ لَيْسَ عَلَيْكُمْ جُنَاحٌ أَنْ تَأْكُلُوا جَمِيعًا أَوْ أَشْتَاتًا ۚ فَإِذَا دَخَلْتُمْ بُيُوتًا فَسَلِّمُوا عَلَىٰ أَنْفُسِكُمْ تَحِيَّةً مِنْ عِنْدِ اللَّهِ مُبَارَكَةً طَيِّبَةً ۚ كَذَٰلِكَ يُبَيِّنُ اللَّهُ لَكُمُ الْآيَاتِ لَعَلَّكُمْ تَعْقِلُونَ', NULL, 60, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_62', 'quran24verse62', 'Quran 24:62', 'quran-24-verse-62', '(Original Book)', 'ara', 'إِنَّمَا الْمُؤْمِنُونَ الَّذِينَ آمَنُوا بِاللَّهِ وَرَسُولِهِ وَإِذَا كَانُوا مَعَهُ عَلَىٰ أَمْرٍ جَامِعٍ لَمْ يَذْهَبُوا حَتَّىٰ يَسْتَأْذِنُوهُ ۚ إِنَّ الَّذِينَ يَسْتَأْذِنُونَكَ أُولَٰئِكَ الَّذِينَ يُؤْمِنُونَ بِاللَّهِ وَرَسُولِهِ ۚ فَإِذَا اسْتَأْذَنُوكَ لِبَعْضِ شَأْنِهِمْ فَأْذَنْ لِمَنْ شِئْتَ مِنْهُمْ وَاسْتَغْفِرْ لَهُمُ اللَّهَ ۚ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', NULL, 61, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_63', 'quran24verse63', 'Quran 24:63', 'quran-24-verse-63', '(Original Book)', 'ara', 'لَا تَجْعَلُوا دُعَاءَ الرَّسُولِ بَيْنَكُمْ كَدُعَاءِ بَعْضِكُمْ بَعْضًا ۚ قَدْ يَعْلَمُ اللَّهُ الَّذِينَ يَتَسَلَّلُونَ مِنْكُمْ لِوَاذًا ۚ فَلْيَحْذَرِ الَّذِينَ يُخَالِفُونَ عَنْ أَمْرِهِ أَنْ تُصِيبَهُمْ فِتْنَةٌ أَوْ يُصِيبَهُمْ عَذَابٌ أَلِيمٌ', NULL, 62, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_24_verse_64', 'quran24verse64', 'Quran 24:64', 'quran-24-verse-64', '(Original Book)', 'ara', 'أَلَا إِنَّ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ قَدْ يَعْلَمُ مَا أَنْتُمْ عَلَيْهِ وَيَوْمَ يُرْجَعُونَ إِلَيْهِ فَيُنَبِّئُهُمْ بِمَا عَمِلُوا ۗ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ', NULL, 63, 'quran_24');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_1', 'quran25verse1', 'Quran 25:1', 'quran-25-verse-1', '(Original Book)', 'ara', 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ تَبَارَكَ الَّذِي نَزَّلَ الْفُرْقَانَ عَلَىٰ عَبْدِهِ لِيَكُونَ لِلْعَالَمِينَ نَذِيرًا', NULL, 0, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_2', 'quran25verse2', 'Quran 25:2', 'quran-25-verse-2', '(Original Book)', 'ara', 'الَّذِي لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ وَلَمْ يَتَّخِذْ وَلَدًا وَلَمْ يَكُنْ لَهُ شَرِيكٌ فِي الْمُلْكِ وَخَلَقَ كُلَّ شَيْءٍ فَقَدَّرَهُ تَقْدِيرًا', NULL, 1, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_3', 'quran25verse3', 'Quran 25:3', 'quran-25-verse-3', '(Original Book)', 'ara', 'وَاتَّخَذُوا مِنْ دُونِهِ آلِهَةً لَا يَخْلُقُونَ شَيْئًا وَهُمْ يُخْلَقُونَ وَلَا يَمْلِكُونَ لِأَنْفُسِهِمْ ضَرًّا وَلَا نَفْعًا وَلَا يَمْلِكُونَ مَوْتًا وَلَا حَيَاةً وَلَا نُشُورًا', NULL, 2, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_4', 'quran25verse4', 'Quran 25:4', 'quran-25-verse-4', '(Original Book)', 'ara', 'وَقَالَ الَّذِينَ كَفَرُوا إِنْ هَٰذَا إِلَّا إِفْكٌ افْتَرَاهُ وَأَعَانَهُ عَلَيْهِ قَوْمٌ آخَرُونَ ۖ فَقَدْ جَاءُوا ظُلْمًا وَزُورًا', NULL, 3, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_5', 'quran25verse5', 'Quran 25:5', 'quran-25-verse-5', '(Original Book)', 'ara', 'وَقَالُوا أَسَاطِيرُ الْأَوَّلِينَ اكْتَتَبَهَا فَهِيَ تُمْلَىٰ عَلَيْهِ بُكْرَةً وَأَصِيلًا', NULL, 4, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_6', 'quran25verse6', 'Quran 25:6', 'quran-25-verse-6', '(Original Book)', 'ara', 'قُلْ أَنْزَلَهُ الَّذِي يَعْلَمُ السِّرَّ فِي السَّمَاوَاتِ وَالْأَرْضِ ۚ إِنَّهُ كَانَ غَفُورًا رَحِيمًا', NULL, 5, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_31', 'quran25verse31', 'Quran 25:31', 'quran-25-verse-31', '(Original Book)', 'ara', 'وَكَذَٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا مِنَ الْمُجْرِمِينَ ۗ وَكَفَىٰ بِرَبِّكَ هَادِيًا وَنَصِيرًا', NULL, 30, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_8', 'quran25verse8', 'Quran 25:8', 'quran-25-verse-8', '(Original Book)', 'ara', 'أَوْ يُلْقَىٰ إِلَيْهِ كَنْزٌ أَوْ تَكُونُ لَهُ جَنَّةٌ يَأْكُلُ مِنْهَا ۚ وَقَالَ الظَّالِمُونَ إِنْ تَتَّبِعُونَ إِلَّا رَجُلًا مَسْحُورًا', NULL, 7, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_9', 'quran25verse9', 'Quran 25:9', 'quran-25-verse-9', '(Original Book)', 'ara', 'انْظُرْ كَيْفَ ضَرَبُوا لَكَ الْأَمْثَالَ فَضَلُّوا فَلَا يَسْتَطِيعُونَ سَبِيلًا', NULL, 8, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_10', 'quran25verse10', 'Quran 25:10', 'quran-25-verse-10', '(Original Book)', 'ara', 'تَبَارَكَ الَّذِي إِنْ شَاءَ جَعَلَ لَكَ خَيْرًا مِنْ ذَٰلِكَ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ وَيَجْعَلْ لَكَ قُصُورًا', NULL, 9, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_11', 'quran25verse11', 'Quran 25:11', 'quran-25-verse-11', '(Original Book)', 'ara', 'بَلْ كَذَّبُوا بِالسَّاعَةِ ۖ وَأَعْتَدْنَا لِمَنْ كَذَّبَ بِالسَّاعَةِ سَعِيرًا', NULL, 10, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_12', 'quran25verse12', 'Quran 25:12', 'quran-25-verse-12', '(Original Book)', 'ara', 'إِذَا رَأَتْهُمْ مِنْ مَكَانٍ بَعِيدٍ سَمِعُوا لَهَا تَغَيُّظًا وَزَفِيرًا', NULL, 11, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_13', 'quran25verse13', 'Quran 25:13', 'quran-25-verse-13', '(Original Book)', 'ara', 'وَإِذَا أُلْقُوا مِنْهَا مَكَانًا ضَيِّقًا مُقَرَّنِينَ دَعَوْا هُنَالِكَ ثُبُورًا', NULL, 12, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_14', 'quran25verse14', 'Quran 25:14', 'quran-25-verse-14', '(Original Book)', 'ara', 'لَا تَدْعُوا الْيَوْمَ ثُبُورًا وَاحِدًا وَادْعُوا ثُبُورًا كَثِيرًا', NULL, 13, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_15', 'quran25verse15', 'Quran 25:15', 'quran-25-verse-15', '(Original Book)', 'ara', 'قُلْ أَذَٰلِكَ خَيْرٌ أَمْ جَنَّةُ الْخُلْدِ الَّتِي وُعِدَ الْمُتَّقُونَ ۚ كَانَتْ لَهُمْ جَزَاءً وَمَصِيرًا', NULL, 14, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_16', 'quran25verse16', 'Quran 25:16', 'quran-25-verse-16', '(Original Book)', 'ara', 'لَهُمْ فِيهَا مَا يَشَاءُونَ خَالِدِينَ ۚ كَانَ عَلَىٰ رَبِّكَ وَعْدًا مَسْئُولًا', NULL, 15, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_17', 'quran25verse17', 'Quran 25:17', 'quran-25-verse-17', '(Original Book)', 'ara', 'وَيَوْمَ يَحْشُرُهُمْ وَمَا يَعْبُدُونَ مِنْ دُونِ اللَّهِ فَيَقُولُ أَأَنْتُمْ أَضْلَلْتُمْ عِبَادِي هَٰؤُلَاءِ أَمْ هُمْ ضَلُّوا السَّبِيلَ', NULL, 16, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_18', 'quran25verse18', 'Quran 25:18', 'quran-25-verse-18', '(Original Book)', 'ara', 'قَالُوا سُبْحَانَكَ مَا كَانَ يَنْبَغِي لَنَا أَنْ نَتَّخِذَ مِنْ دُونِكَ مِنْ أَوْلِيَاءَ وَلَٰكِنْ مَتَّعْتَهُمْ وَآبَاءَهُمْ حَتَّىٰ نَسُوا الذِّكْرَ وَكَانُوا قَوْمًا بُورًا', NULL, 17, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_19', 'quran25verse19', 'Quran 25:19', 'quran-25-verse-19', '(Original Book)', 'ara', 'فَقَدْ كَذَّبُوكُمْ بِمَا تَقُولُونَ فَمَا تَسْتَطِيعُونَ صَرْفًا وَلَا نَصْرًا ۚ وَمَنْ يَظْلِمْ مِنْكُمْ نُذِقْهُ عَذَابًا كَبِيرًا', NULL, 18, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_20', 'quran25verse20', 'Quran 25:20', 'quran-25-verse-20', '(Original Book)', 'ara', 'وَمَا أَرْسَلْنَا قَبْلَكَ مِنَ الْمُرْسَلِينَ إِلَّا إِنَّهُمْ لَيَأْكُلُونَ الطَّعَامَ وَيَمْشُونَ فِي الْأَسْوَاقِ ۗ وَجَعَلْنَا بَعْضَكُمْ لِبَعْضٍ فِتْنَةً أَتَصْبِرُونَ ۗ وَكَانَ رَبُّكَ بَصِيرًا', NULL, 19, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_21', 'quran25verse21', 'Quran 25:21', 'quran-25-verse-21', '(Original Book)', 'ara', 'وَقَالَ الَّذِينَ لَا يَرْجُونَ لِقَاءَنَا لَوْلَا أُنْزِلَ عَلَيْنَا الْمَلَائِكَةُ أَوْ نَرَىٰ رَبَّنَا ۗ لَقَدِ اسْتَكْبَرُوا فِي أَنْفُسِهِمْ وَعَتَوْا عُتُوًّا كَبِيرًا', NULL, 20, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_22', 'quran25verse22', 'Quran 25:22', 'quran-25-verse-22', '(Original Book)', 'ara', 'يَوْمَ يَرَوْنَ الْمَلَائِكَةَ لَا بُشْرَىٰ يَوْمَئِذٍ لِلْمُجْرِمِينَ وَيَقُولُونَ حِجْرًا مَحْجُورًا', NULL, 21, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_23', 'quran25verse23', 'Quran 25:23', 'quran-25-verse-23', '(Original Book)', 'ara', 'وَقَدِمْنَا إِلَىٰ مَا عَمِلُوا مِنْ عَمَلٍ فَجَعَلْنَاهُ هَبَاءً مَنْثُورًا', NULL, 22, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_24', 'quran25verse24', 'Quran 25:24', 'quran-25-verse-24', '(Original Book)', 'ara', 'أَصْحَابُ الْجَنَّةِ يَوْمَئِذٍ خَيْرٌ مُسْتَقَرًّا وَأَحْسَنُ مَقِيلًا', NULL, 23, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_25', 'quran25verse25', 'Quran 25:25', 'quran-25-verse-25', '(Original Book)', 'ara', 'وَيَوْمَ تَشَقَّقُ السَّمَاءُ بِالْغَمَامِ وَنُزِّلَ الْمَلَائِكَةُ تَنْزِيلًا', NULL, 24, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_26', 'quran25verse26', 'Quran 25:26', 'quran-25-verse-26', '(Original Book)', 'ara', 'الْمُلْكُ يَوْمَئِذٍ الْحَقُّ لِلرَّحْمَٰنِ ۚ وَكَانَ يَوْمًا عَلَى الْكَافِرِينَ عَسِيرًا', NULL, 25, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_27', 'quran25verse27', 'Quran 25:27', 'quran-25-verse-27', '(Original Book)', 'ara', 'وَيَوْمَ يَعَضُّ الظَّالِمُ عَلَىٰ يَدَيْهِ يَقُولُ يَا لَيْتَنِي اتَّخَذْتُ مَعَ الرَّسُولِ سَبِيلًا', NULL, 26, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_28', 'quran25verse28', 'Quran 25:28', 'quran-25-verse-28', '(Original Book)', 'ara', 'يَا وَيْلَتَىٰ لَيْتَنِي لَمْ أَتَّخِذْ فُلَانًا خَلِيلًا', NULL, 27, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_29', 'quran25verse29', 'Quran 25:29', 'quran-25-verse-29', '(Original Book)', 'ara', 'لَقَدْ أَضَلَّنِي عَنِ الذِّكْرِ بَعْدَ إِذْ جَاءَنِي ۗ وَكَانَ الشَّيْطَانُ لِلْإِنْسَانِ خَذُولًا', NULL, 28, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_30', 'quran25verse30', 'Quran 25:30', 'quran-25-verse-30', '(Original Book)', 'ara', 'وَقَالَ الرَّسُولُ يَا رَبِّ إِنَّ قَوْمِي اتَّخَذُوا هَٰذَا الْقُرْآنَ مَهْجُورًا', NULL, 29, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_56', 'quran25verse56', 'Quran 25:56', 'quran-25-verse-56', '(Original Book)', 'ara', 'وَمَا أَرْسَلْنَاكَ إِلَّا مُبَشِّرًا وَنَذِيرًا', NULL, 55, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_32', 'quran25verse32', 'Quran 25:32', 'quran-25-verse-32', '(Original Book)', 'ara', 'وَقَالَ الَّذِينَ كَفَرُوا لَوْلَا نُزِّلَ عَلَيْهِ الْقُرْآنُ جُمْلَةً وَاحِدَةً ۚ كَذَٰلِكَ لِنُثَبِّتَ بِهِ فُؤَادَكَ ۖ وَرَتَّلْنَاهُ تَرْتِيلًا', NULL, 31, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_33', 'quran25verse33', 'Quran 25:33', 'quran-25-verse-33', '(Original Book)', 'ara', 'وَلَا يَأْتُونَكَ بِمَثَلٍ إِلَّا جِئْنَاكَ بِالْحَقِّ وَأَحْسَنَ تَفْسِيرًا', NULL, 32, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_34', 'quran25verse34', 'Quran 25:34', 'quran-25-verse-34', '(Original Book)', 'ara', 'الَّذِينَ يُحْشَرُونَ عَلَىٰ وُجُوهِهِمْ إِلَىٰ جَهَنَّمَ أُولَٰئِكَ شَرٌّ مَكَانًا وَأَضَلُّ سَبِيلًا', NULL, 33, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_35', 'quran25verse35', 'Quran 25:35', 'quran-25-verse-35', '(Original Book)', 'ara', 'وَلَقَدْ آتَيْنَا مُوسَى الْكِتَابَ وَجَعَلْنَا مَعَهُ أَخَاهُ هَارُونَ وَزِيرًا', NULL, 34, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_36', 'quran25verse36', 'Quran 25:36', 'quran-25-verse-36', '(Original Book)', 'ara', 'فَقُلْنَا اذْهَبَا إِلَى الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِنَا فَدَمَّرْنَاهُمْ تَدْمِيرًا', NULL, 35, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_37', 'quran25verse37', 'Quran 25:37', 'quran-25-verse-37', '(Original Book)', 'ara', 'وَقَوْمَ نُوحٍ لَمَّا كَذَّبُوا الرُّسُلَ أَغْرَقْنَاهُمْ وَجَعَلْنَاهُمْ لِلنَّاسِ آيَةً ۖ وَأَعْتَدْنَا لِلظَّالِمِينَ عَذَابًا أَلِيمًا', NULL, 36, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_38', 'quran25verse38', 'Quran 25:38', 'quran-25-verse-38', '(Original Book)', 'ara', 'وَعَادًا وَثَمُودَ وَأَصْحَابَ الرَّسِّ وَقُرُونًا بَيْنَ ذَٰلِكَ كَثِيرًا', NULL, 37, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_39', 'quran25verse39', 'Quran 25:39', 'quran-25-verse-39', '(Original Book)', 'ara', 'وَكُلًّا ضَرَبْنَا لَهُ الْأَمْثَالَ ۖ وَكُلًّا تَبَّرْنَا تَتْبِيرًا', NULL, 38, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_40', 'quran25verse40', 'Quran 25:40', 'quran-25-verse-40', '(Original Book)', 'ara', 'وَلَقَدْ أَتَوْا عَلَى الْقَرْيَةِ الَّتِي أُمْطِرَتْ مَطَرَ السَّوْءِ ۚ أَفَلَمْ يَكُونُوا يَرَوْنَهَا ۚ بَلْ كَانُوا لَا يَرْجُونَ نُشُورًا', NULL, 39, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_41', 'quran25verse41', 'Quran 25:41', 'quran-25-verse-41', '(Original Book)', 'ara', 'وَإِذَا رَأَوْكَ إِنْ يَتَّخِذُونَكَ إِلَّا هُزُوًا أَهَٰذَا الَّذِي بَعَثَ اللَّهُ رَسُولًا', NULL, 40, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_42', 'quran25verse42', 'Quran 25:42', 'quran-25-verse-42', '(Original Book)', 'ara', 'إِنْ كَادَ لَيُضِلُّنَا عَنْ آلِهَتِنَا لَوْلَا أَنْ صَبَرْنَا عَلَيْهَا ۚ وَسَوْفَ يَعْلَمُونَ حِينَ يَرَوْنَ الْعَذَابَ مَنْ أَضَلُّ سَبِيلًا', NULL, 41, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_43', 'quran25verse43', 'Quran 25:43', 'quran-25-verse-43', '(Original Book)', 'ara', 'أَرَأَيْتَ مَنِ اتَّخَذَ إِلَٰهَهُ هَوَاهُ أَفَأَنْتَ تَكُونُ عَلَيْهِ وَكِيلًا', NULL, 42, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_44', 'quran25verse44', 'Quran 25:44', 'quran-25-verse-44', '(Original Book)', 'ara', 'أَمْ تَحْسَبُ أَنَّ أَكْثَرَهُمْ يَسْمَعُونَ أَوْ يَعْقِلُونَ ۚ إِنْ هُمْ إِلَّا كَالْأَنْعَامِ ۖ بَلْ هُمْ أَضَلُّ سَبِيلًا', NULL, 43, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_45', 'quran25verse45', 'Quran 25:45', 'quran-25-verse-45', '(Original Book)', 'ara', 'أَلَمْ تَرَ إِلَىٰ رَبِّكَ كَيْفَ مَدَّ الظِّلَّ وَلَوْ شَاءَ لَجَعَلَهُ سَاكِنًا ثُمَّ جَعَلْنَا الشَّمْسَ عَلَيْهِ دَلِيلًا', NULL, 44, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_46', 'quran25verse46', 'Quran 25:46', 'quran-25-verse-46', '(Original Book)', 'ara', 'ثُمَّ قَبَضْنَاهُ إِلَيْنَا قَبْضًا يَسِيرًا', NULL, 45, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_47', 'quran25verse47', 'Quran 25:47', 'quran-25-verse-47', '(Original Book)', 'ara', 'وَهُوَ الَّذِي جَعَلَ لَكُمُ اللَّيْلَ لِبَاسًا وَالنَّوْمَ سُبَاتًا وَجَعَلَ النَّهَارَ نُشُورًا', NULL, 46, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_48', 'quran25verse48', 'Quran 25:48', 'quran-25-verse-48', '(Original Book)', 'ara', 'وَهُوَ الَّذِي أَرْسَلَ الرِّيَاحَ بُشْرًا بَيْنَ يَدَيْ رَحْمَتِهِ ۚ وَأَنْزَلْنَا مِنَ السَّمَاءِ مَاءً طَهُورًا', NULL, 47, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_49', 'quran25verse49', 'Quran 25:49', 'quran-25-verse-49', '(Original Book)', 'ara', 'لِنُحْيِيَ بِهِ بَلْدَةً مَيْتًا وَنُسْقِيَهُ مِمَّا خَلَقْنَا أَنْعَامًا وَأَنَاسِيَّ كَثِيرًا', NULL, 48, 'quran_25');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_25_verse_50', 'quran25verse50', 'Quran 25:50', 'quran-25-verse-50', '(Original Book)', 'ara', 'وَلَقَدْ صَرَّفْنَاهُ بَيْنَهُمْ لِيَذَّكَّرُوا فَأَبَىٰ أَكْثَرُ النَّاسِ إِلَّا كُفُورًا', NULL, 49, 'quran_25');
