INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_38_verse_24', 'quran38verse24', 'Quran 38:24', 'quran-38-verse-24', '(Original Book)', 'ara', 'قَالَ لَقَدْ ظَلَمَكَ بِسُؤَالِ نَعْجَتِكَ إِلَىٰ نِعَاجِهِ ۖ وَإِنَّ كَثِيرًا مِنَ الْخُلَطَاءِ لَيَبْغِي بَعْضُهُمْ عَلَىٰ بَعْضٍ إِلَّا الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ وَقَلِيلٌ مَا هُمْ ۗ وَظَنَّ دَاوُودُ أَنَّمَا فَتَنَّاهُ فَاسْتَغْفَرَ رَبَّهُ وَخَرَّ رَاكِعًا وَأَنَابَ ۩', NULL, 23, 'quran_38');
