INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_13', 'quran43verse13', 'Quran 43:13', 'quran-43-verse-13', '(Original Book)', 'ara', 'لِتَسْتَوُوا عَلَىٰ ظُهُورِهِ ثُمَّ تَذْكُرُوا نِعْمَةَ رَبِّكُمْ إِذَا اسْتَوَيْتُمْ عَلَيْهِ وَتَقُولُوا سُبْحَانَ الَّذِي سَخَّرَ لَنَا هَٰذَا وَمَا كُنَّا لَهُ مُقْرِنِينَ', NULL, 12, 'quran_43');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_14', 'quran43verse14', 'Quran 43:14', 'quran-43-verse-14', '(Original Book)', 'ara', 'وَإِنَّا إِلَىٰ رَبِّنَا لَمُنْقَلِبُونَ', NULL, 13, 'quran_43');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_15', 'quran43verse15', 'Quran 43:15', 'quran-43-verse-15', '(Original Book)', 'ara', 'وَجَعَلُوا لَهُ مِنْ عِبَادِهِ جُزْءًا ۚ إِنَّ الْإِنْسَانَ لَكَفُورٌ مُبِينٌ', NULL, 14, 'quran_43');
