INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_25', 'quran43verse25', 'Quran 43:25', 'quran-43-verse-25', '(Original Book)', 'ara', 'فَانْتَقَمْنَا مِنْهُمْ ۖ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِينَ', NULL, 24, 'quran_43');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_26', 'quran43verse26', 'Quran 43:26', 'quran-43-verse-26', '(Original Book)', 'ara', 'وَإِذْ قَالَ إِبْرَاهِيمُ لِأَبِيهِ وَقَوْمِهِ إِنَّنِي بَرَاءٌ مِمَّا تَعْبُدُونَ', NULL, 25, 'quran_43');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_27', 'quran43verse27', 'Quran 43:27', 'quran-43-verse-27', '(Original Book)', 'ara', 'إِلَّا الَّذِي فَطَرَنِي فَإِنَّهُ سَيَهْدِينِ', NULL, 26, 'quran_43');
