INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_30', 'quran43verse30', 'Quran 43:30', 'quran-43-verse-30', '(Original Book)', 'ara', 'وَلَمَّا جَاءَهُمُ الْحَقُّ قَالُوا هَٰذَا سِحْرٌ وَإِنَّا بِهِ كَافِرُونَ', NULL, 29, 'quran_43');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_31', 'quran43verse31', 'Quran 43:31', 'quran-43-verse-31', '(Original Book)', 'ara', 'وَقَالُوا لَوْلَا نُزِّلَ هَٰذَا الْقُرْآنُ عَلَىٰ رَجُلٍ مِنَ الْقَرْيَتَيْنِ عَظِيمٍ', NULL, 30, 'quran_43');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_32', 'quran43verse32', 'Quran 43:32', 'quran-43-verse-32', '(Original Book)', 'ara', 'أَهُمْ يَقْسِمُونَ رَحْمَتَ رَبِّكَ ۚ نَحْنُ قَسَمْنَا بَيْنَهُمْ مَعِيشَتَهُمْ فِي الْحَيَاةِ الدُّنْيَا ۚ وَرَفَعْنَا بَعْضَهُمْ فَوْقَ بَعْضٍ دَرَجَاتٍ لِيَتَّخِذَ بَعْضُهُمْ بَعْضًا سُخْرِيًّا ۗ وَرَحْمَتُ رَبِّكَ خَيْرٌ مِمَّا يَجْمَعُونَ', NULL, 31, 'quran_43');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_33', 'quran43verse33', 'Quran 43:33', 'quran-43-verse-33', '(Original Book)', 'ara', 'وَلَوْلَا أَنْ يَكُونَ النَّاسُ أُمَّةً وَاحِدَةً لَجَعَلْنَا لِمَنْ يَكْفُرُ بِالرَّحْمَٰنِ لِبُيُوتِهِمْ سُقُفًا مِنْ فِضَّةٍ وَمَعَارِجَ عَلَيْهَا يَظْهَرُونَ', NULL, 32, 'quran_43');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_34', 'quran43verse34', 'Quran 43:34', 'quran-43-verse-34', '(Original Book)', 'ara', 'وَلِبُيُوتِهِمْ أَبْوَابًا وَسُرُرًا عَلَيْهَا يَتَّكِئُونَ', NULL, 33, 'quran_43');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_35', 'quran43verse35', 'Quran 43:35', 'quran-43-verse-35', '(Original Book)', 'ara', 'وَزُخْرُفًا ۚ وَإِنْ كُلُّ ذَٰلِكَ لَمَّا مَتَاعُ الْحَيَاةِ الدُّنْيَا ۚ وَالْآخِرَةُ عِنْدَ رَبِّكَ لِلْمُتَّقِينَ', NULL, 34, 'quran_43');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_36', 'quran43verse36', 'Quran 43:36', 'quran-43-verse-36', '(Original Book)', 'ara', 'وَمَنْ يَعْشُ عَنْ ذِكْرِ الرَّحْمَٰنِ نُقَيِّضْ لَهُ شَيْطَانًا فَهُوَ لَهُ قَرِينٌ', NULL, 35, 'quran_43');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_37', 'quran43verse37', 'Quran 43:37', 'quran-43-verse-37', '(Original Book)', 'ara', 'وَإِنَّهُمْ لَيَصُدُّونَهُمْ عَنِ السَّبِيلِ وَيَحْسَبُونَ أَنَّهُمْ مُهْتَدُونَ', NULL, 36, 'quran_43');
