INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_43_verse_40', 'quran43verse40', 'Quran 43:40', 'quran-43-verse-40', '(Original Book)', 'ara', 'أَفَأَنْتَ تُسْمِعُ الصُّمَّ أَوْ تَهْدِي الْعُمْيَ وَمَنْ كَانَ فِي ضَلَالٍ مُبِينٍ', NULL, 39, 'quran_43');
