INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_1', 'quran57verse1', 'Quran 57:1', 'quran-57-verse-1', '(Original Book)', 'ara', 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ وَهُوَ الْعَزِيزُ الْحَكِيمُ', NULL, 0, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_2', 'quran57verse2', 'Quran 57:2', 'quran-57-verse-2', '(Original Book)', 'ara', 'لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۖ يُحْيِي وَيُمِيتُ ۖ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', NULL, 1, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_3', 'quran57verse3', 'Quran 57:3', 'quran-57-verse-3', '(Original Book)', 'ara', 'هُوَ الْأَوَّلُ وَالْآخِرُ وَالظَّاهِرُ وَالْبَاطِنُ ۖ وَهُوَ بِكُلِّ شَيْءٍ عَلِيمٌ', NULL, 2, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_4', 'quran57verse4', 'Quran 57:4', 'quran-57-verse-4', '(Original Book)', 'ara', 'هُوَ الَّذِي خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ فِي سِتَّةِ أَيَّامٍ ثُمَّ اسْتَوَىٰ عَلَى الْعَرْشِ ۚ يَعْلَمُ مَا يَلِجُ فِي الْأَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَاءِ وَمَا يَعْرُجُ فِيهَا ۖ وَهُوَ مَعَكُمْ أَيْنَ مَا كُنْتُمْ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', NULL, 3, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_5', 'quran57verse5', 'Quran 57:5', 'quran-57-verse-5', '(Original Book)', 'ara', 'لَهُ مُلْكُ السَّمَاوَاتِ وَالْأَرْضِ ۚ وَإِلَى اللَّهِ تُرْجَعُ الْأُمُورُ', NULL, 4, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_6', 'quran57verse6', 'Quran 57:6', 'quran-57-verse-6', '(Original Book)', 'ara', 'يُولِجُ اللَّيْلَ فِي النَّهَارِ وَيُولِجُ النَّهَارَ فِي اللَّيْلِ ۚ وَهُوَ عَلِيمٌ بِذَاتِ الصُّدُورِ', NULL, 5, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_7', 'quran57verse7', 'Quran 57:7', 'quran-57-verse-7', '(Original Book)', 'ara', 'آمِنُوا بِاللَّهِ وَرَسُولِهِ وَأَنْفِقُوا مِمَّا جَعَلَكُمْ مُسْتَخْلَفِينَ فِيهِ ۖ فَالَّذِينَ آمَنُوا مِنْكُمْ وَأَنْفَقُوا لَهُمْ أَجْرٌ كَبِيرٌ', NULL, 6, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_8', 'quran57verse8', 'Quran 57:8', 'quran-57-verse-8', '(Original Book)', 'ara', 'وَمَا لَكُمْ لَا تُؤْمِنُونَ بِاللَّهِ ۙ وَالرَّسُولُ يَدْعُوكُمْ لِتُؤْمِنُوا بِرَبِّكُمْ وَقَدْ أَخَذَ مِيثَاقَكُمْ إِنْ كُنْتُمْ مُؤْمِنِينَ', NULL, 7, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_9', 'quran57verse9', 'Quran 57:9', 'quran-57-verse-9', '(Original Book)', 'ara', 'هُوَ الَّذِي يُنَزِّلُ عَلَىٰ عَبْدِهِ آيَاتٍ بَيِّنَاتٍ لِيُخْرِجَكُمْ مِنَ الظُّلُمَاتِ إِلَى النُّورِ ۚ وَإِنَّ اللَّهَ بِكُمْ لَرَءُوفٌ رَحِيمٌ', NULL, 8, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_10', 'quran57verse10', 'Quran 57:10', 'quran-57-verse-10', '(Original Book)', 'ara', 'وَمَا لَكُمْ أَلَّا تُنْفِقُوا فِي سَبِيلِ اللَّهِ وَلِلَّهِ مِيرَاثُ السَّمَاوَاتِ وَالْأَرْضِ ۚ لَا يَسْتَوِي مِنْكُمْ مَنْ أَنْفَقَ مِنْ قَبْلِ الْفَتْحِ وَقَاتَلَ ۚ أُولَٰئِكَ أَعْظَمُ دَرَجَةً مِنَ الَّذِينَ أَنْفَقُوا مِنْ بَعْدُ وَقَاتَلُوا ۚ وَكُلًّا وَعَدَ اللَّهُ الْحُسْنَىٰ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', NULL, 9, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_11', 'quran57verse11', 'Quran 57:11', 'quran-57-verse-11', '(Original Book)', 'ara', 'مَنْ ذَا الَّذِي يُقْرِضُ اللَّهَ قَرْضًا حَسَنًا فَيُضَاعِفَهُ لَهُ وَلَهُ أَجْرٌ كَرِيمٌ', NULL, 10, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_12', 'quran57verse12', 'Quran 57:12', 'quran-57-verse-12', '(Original Book)', 'ara', 'يَوْمَ تَرَى الْمُؤْمِنِينَ وَالْمُؤْمِنَاتِ يَسْعَىٰ نُورُهُمْ بَيْنَ أَيْدِيهِمْ وَبِأَيْمَانِهِمْ بُشْرَاكُمُ الْيَوْمَ جَنَّاتٌ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ ذَٰلِكَ هُوَ الْفَوْزُ الْعَظِيمُ', NULL, 11, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_13', 'quran57verse13', 'Quran 57:13', 'quran-57-verse-13', '(Original Book)', 'ara', 'يَوْمَ يَقُولُ الْمُنَافِقُونَ وَالْمُنَافِقَاتُ لِلَّذِينَ آمَنُوا انْظُرُونَا نَقْتَبِسْ مِنْ نُورِكُمْ قِيلَ ارْجِعُوا وَرَاءَكُمْ فَالْتَمِسُوا نُورًا فَضُرِبَ بَيْنَهُمْ بِسُورٍ لَهُ بَابٌ بَاطِنُهُ فِيهِ الرَّحْمَةُ وَظَاهِرُهُ مِنْ قِبَلِهِ الْعَذَابُ', NULL, 12, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_14', 'quran57verse14', 'Quran 57:14', 'quran-57-verse-14', '(Original Book)', 'ara', 'يُنَادُونَهُمْ أَلَمْ نَكُنْ مَعَكُمْ ۖ قَالُوا بَلَىٰ وَلَٰكِنَّكُمْ فَتَنْتُمْ أَنْفُسَكُمْ وَتَرَبَّصْتُمْ وَارْتَبْتُمْ وَغَرَّتْكُمُ الْأَمَانِيُّ حَتَّىٰ جَاءَ أَمْرُ اللَّهِ وَغَرَّكُمْ بِاللَّهِ الْغَرُورُ', NULL, 13, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_15', 'quran57verse15', 'Quran 57:15', 'quran-57-verse-15', '(Original Book)', 'ara', 'فَالْيَوْمَ لَا يُؤْخَذُ مِنْكُمْ فِدْيَةٌ وَلَا مِنَ الَّذِينَ كَفَرُوا ۚ مَأْوَاكُمُ النَّارُ ۖ هِيَ مَوْلَاكُمْ ۖ وَبِئْسَ الْمَصِيرُ', NULL, 14, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_60_verse_3', 'quran60verse3', 'Quran 60:3', 'quran-60-verse-3', '(Original Book)', 'ara', 'لَنْ تَنْفَعَكُمْ أَرْحَامُكُمْ وَلَا أَوْلَادُكُمْ ۚ يَوْمَ الْقِيَامَةِ يَفْصِلُ بَيْنَكُمْ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', NULL, 2, 'quran_60');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_16', 'quran57verse16', 'Quran 57:16', 'quran-57-verse-16', '(Original Book)', 'ara', 'أَلَمْ يَأْنِ لِلَّذِينَ آمَنُوا أَنْ تَخْشَعَ قُلُوبُهُمْ لِذِكْرِ اللَّهِ وَمَا نَزَلَ مِنَ الْحَقِّ وَلَا يَكُونُوا كَالَّذِينَ أُوتُوا الْكِتَابَ مِنْ قَبْلُ فَطَالَ عَلَيْهِمُ الْأَمَدُ فَقَسَتْ قُلُوبُهُمْ ۖ وَكَثِيرٌ مِنْهُمْ فَاسِقُونَ', NULL, 15, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_17', 'quran57verse17', 'Quran 57:17', 'quran-57-verse-17', '(Original Book)', 'ara', 'اعْلَمُوا أَنَّ اللَّهَ يُحْيِي الْأَرْضَ بَعْدَ مَوْتِهَا ۚ قَدْ بَيَّنَّا لَكُمُ الْآيَاتِ لَعَلَّكُمْ تَعْقِلُونَ', NULL, 16, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_18', 'quran57verse18', 'Quran 57:18', 'quran-57-verse-18', '(Original Book)', 'ara', 'إِنَّ الْمُصَّدِّقِينَ وَالْمُصَّدِّقَاتِ وَأَقْرَضُوا اللَّهَ قَرْضًا حَسَنًا يُضَاعَفُ لَهُمْ وَلَهُمْ أَجْرٌ كَرِيمٌ', NULL, 17, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_19', 'quran57verse19', 'Quran 57:19', 'quran-57-verse-19', '(Original Book)', 'ara', 'وَالَّذِينَ آمَنُوا بِاللَّهِ وَرُسُلِهِ أُولَٰئِكَ هُمُ الصِّدِّيقُونَ ۖ وَالشُّهَدَاءُ عِنْدَ رَبِّهِمْ لَهُمْ أَجْرُهُمْ وَنُورُهُمْ ۖ وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَٰئِكَ أَصْحَابُ الْجَحِيمِ', NULL, 18, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_20', 'quran57verse20', 'Quran 57:20', 'quran-57-verse-20', '(Original Book)', 'ara', 'اعْلَمُوا أَنَّمَا الْحَيَاةُ الدُّنْيَا لَعِبٌ وَلَهْوٌ وَزِينَةٌ وَتَفَاخُرٌ بَيْنَكُمْ وَتَكَاثُرٌ فِي الْأَمْوَالِ وَالْأَوْلَادِ ۖ كَمَثَلِ غَيْثٍ أَعْجَبَ الْكُفَّارَ نَبَاتُهُ ثُمَّ يَهِيجُ فَتَرَاهُ مُصْفَرًّا ثُمَّ يَكُونُ حُطَامًا ۖ وَفِي الْآخِرَةِ عَذَابٌ شَدِيدٌ وَمَغْفِرَةٌ مِنَ اللَّهِ وَرِضْوَانٌ ۚ وَمَا الْحَيَاةُ الدُّنْيَا إِلَّا مَتَاعُ الْغُرُورِ', NULL, 19, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_21', 'quran57verse21', 'Quran 57:21', 'quran-57-verse-21', '(Original Book)', 'ara', 'سَابِقُوا إِلَىٰ مَغْفِرَةٍ مِنْ رَبِّكُمْ وَجَنَّةٍ عَرْضُهَا كَعَرْضِ السَّمَاءِ وَالْأَرْضِ أُعِدَّتْ لِلَّذِينَ آمَنُوا بِاللَّهِ وَرُسُلِهِ ۚ ذَٰلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ ۚ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ', NULL, 20, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_22', 'quran57verse22', 'Quran 57:22', 'quran-57-verse-22', '(Original Book)', 'ara', 'مَا أَصَابَ مِنْ مُصِيبَةٍ فِي الْأَرْضِ وَلَا فِي أَنْفُسِكُمْ إِلَّا فِي كِتَابٍ مِنْ قَبْلِ أَنْ نَبْرَأَهَا ۚ إِنَّ ذَٰلِكَ عَلَى اللَّهِ يَسِيرٌ', NULL, 21, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_23', 'quran57verse23', 'Quran 57:23', 'quran-57-verse-23', '(Original Book)', 'ara', 'لِكَيْلَا تَأْسَوْا عَلَىٰ مَا فَاتَكُمْ وَلَا تَفْرَحُوا بِمَا آتَاكُمْ ۗ وَاللَّهُ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُورٍ', NULL, 22, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_24', 'quran57verse24', 'Quran 57:24', 'quran-57-verse-24', '(Original Book)', 'ara', 'الَّذِينَ يَبْخَلُونَ وَيَأْمُرُونَ النَّاسَ بِالْبُخْلِ ۗ وَمَنْ يَتَوَلَّ فَإِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ', NULL, 23, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_25', 'quran57verse25', 'Quran 57:25', 'quran-57-verse-25', '(Original Book)', 'ara', 'لَقَدْ أَرْسَلْنَا رُسُلَنَا بِالْبَيِّنَاتِ وَأَنْزَلْنَا مَعَهُمُ الْكِتَابَ وَالْمِيزَانَ لِيَقُومَ النَّاسُ بِالْقِسْطِ ۖ وَأَنْزَلْنَا الْحَدِيدَ فِيهِ بَأْسٌ شَدِيدٌ وَمَنَافِعُ لِلنَّاسِ وَلِيَعْلَمَ اللَّهُ مَنْ يَنْصُرُهُ وَرُسُلَهُ بِالْغَيْبِ ۚ إِنَّ اللَّهَ قَوِيٌّ عَزِيزٌ', NULL, 24, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_26', 'quran57verse26', 'Quran 57:26', 'quran-57-verse-26', '(Original Book)', 'ara', 'وَلَقَدْ أَرْسَلْنَا نُوحًا وَإِبْرَاهِيمَ وَجَعَلْنَا فِي ذُرِّيَّتِهِمَا النُّبُوَّةَ وَالْكِتَابَ ۖ فَمِنْهُمْ مُهْتَدٍ ۖ وَكَثِيرٌ مِنْهُمْ فَاسِقُونَ', NULL, 25, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_27', 'quran57verse27', 'Quran 57:27', 'quran-57-verse-27', '(Original Book)', 'ara', 'ثُمَّ قَفَّيْنَا عَلَىٰ آثَارِهِمْ بِرُسُلِنَا وَقَفَّيْنَا بِعِيسَى ابْنِ مَرْيَمَ وَآتَيْنَاهُ الْإِنْجِيلَ وَجَعَلْنَا فِي قُلُوبِ الَّذِينَ اتَّبَعُوهُ رَأْفَةً وَرَحْمَةً وَرَهْبَانِيَّةً ابْتَدَعُوهَا مَا كَتَبْنَاهَا عَلَيْهِمْ إِلَّا ابْتِغَاءَ رِضْوَانِ اللَّهِ فَمَا رَعَوْهَا حَقَّ رِعَايَتِهَا ۖ فَآتَيْنَا الَّذِينَ آمَنُوا مِنْهُمْ أَجْرَهُمْ ۖ وَكَثِيرٌ مِنْهُمْ فَاسِقُونَ', NULL, 26, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_28', 'quran57verse28', 'Quran 57:28', 'quran-57-verse-28', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَآمِنُوا بِرَسُولِهِ يُؤْتِكُمْ كِفْلَيْنِ مِنْ رَحْمَتِهِ وَيَجْعَلْ لَكُمْ نُورًا تَمْشُونَ بِهِ وَيَغْفِرْ لَكُمْ ۚ وَاللَّهُ غَفُورٌ رَحِيمٌ', NULL, 27, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_57_verse_29', 'quran57verse29', 'Quran 57:29', 'quran-57-verse-29', '(Original Book)', 'ara', 'لِئَلَّا يَعْلَمَ أَهْلُ الْكِتَابِ أَلَّا يَقْدِرُونَ عَلَىٰ شَيْءٍ مِنْ فَضْلِ اللَّهِ ۙ وَأَنَّ الْفَضْلَ بِيَدِ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ ۚ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ', NULL, 28, 'quran_57');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_1', 'quran58verse1', 'Quran 58:1', 'quran-58-verse-1', '(Original Book)', 'ara', 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ قَدْ سَمِعَ اللَّهُ قَوْلَ الَّتِي تُجَادِلُكَ فِي زَوْجِهَا وَتَشْتَكِي إِلَى اللَّهِ وَاللَّهُ يَسْمَعُ تَحَاوُرَكُمَا ۚ إِنَّ اللَّهَ سَمِيعٌ بَصِيرٌ', NULL, 0, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_78_verse_4', 'quran78verse4', 'Quran 78:4', 'quran-78-verse-4', '(Original Book)', 'ara', 'كَلَّا سَيَعْلَمُونَ', NULL, 3, 'quran_78');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_2', 'quran58verse2', 'Quran 58:2', 'quran-58-verse-2', '(Original Book)', 'ara', 'الَّذِينَ يُظَاهِرُونَ مِنْكُمْ مِنْ نِسَائِهِمْ مَا هُنَّ أُمَّهَاتِهِمْ ۖ إِنْ أُمَّهَاتُهُمْ إِلَّا اللَّائِي وَلَدْنَهُمْ ۚ وَإِنَّهُمْ لَيَقُولُونَ مُنْكَرًا مِنَ الْقَوْلِ وَزُورًا ۚ وَإِنَّ اللَّهَ لَعَفُوٌّ غَفُورٌ', NULL, 1, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_3', 'quran58verse3', 'Quran 58:3', 'quran-58-verse-3', '(Original Book)', 'ara', 'وَالَّذِينَ يُظَاهِرُونَ مِنْ نِسَائِهِمْ ثُمَّ يَعُودُونَ لِمَا قَالُوا فَتَحْرِيرُ رَقَبَةٍ مِنْ قَبْلِ أَنْ يَتَمَاسَّا ۚ ذَٰلِكُمْ تُوعَظُونَ بِهِ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', NULL, 2, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_4', 'quran58verse4', 'Quran 58:4', 'quran-58-verse-4', '(Original Book)', 'ara', 'فَمَنْ لَمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ مِنْ قَبْلِ أَنْ يَتَمَاسَّا ۖ فَمَنْ لَمْ يَسْتَطِعْ فَإِطْعَامُ سِتِّينَ مِسْكِينًا ۚ ذَٰلِكَ لِتُؤْمِنُوا بِاللَّهِ وَرَسُولِهِ ۚ وَتِلْكَ حُدُودُ اللَّهِ ۗ وَلِلْكَافِرِينَ عَذَابٌ أَلِيمٌ', NULL, 3, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_5', 'quran58verse5', 'Quran 58:5', 'quran-58-verse-5', '(Original Book)', 'ara', 'إِنَّ الَّذِينَ يُحَادُّونَ اللَّهَ وَرَسُولَهُ كُبِتُوا كَمَا كُبِتَ الَّذِينَ مِنْ قَبْلِهِمْ ۚ وَقَدْ أَنْزَلْنَا آيَاتٍ بَيِّنَاتٍ ۚ وَلِلْكَافِرِينَ عَذَابٌ مُهِينٌ', NULL, 4, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_6', 'quran58verse6', 'Quran 58:6', 'quran-58-verse-6', '(Original Book)', 'ara', 'يَوْمَ يَبْعَثُهُمُ اللَّهُ جَمِيعًا فَيُنَبِّئُهُمْ بِمَا عَمِلُوا ۚ أَحْصَاهُ اللَّهُ وَنَسُوهُ ۚ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ شَهِيدٌ', NULL, 5, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_7', 'quran58verse7', 'Quran 58:7', 'quran-58-verse-7', '(Original Book)', 'ara', 'أَلَمْ تَرَ أَنَّ اللَّهَ يَعْلَمُ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۖ مَا يَكُونُ مِنْ نَجْوَىٰ ثَلَاثَةٍ إِلَّا هُوَ رَابِعُهُمْ وَلَا خَمْسَةٍ إِلَّا هُوَ سَادِسُهُمْ وَلَا أَدْنَىٰ مِنْ ذَٰلِكَ وَلَا أَكْثَرَ إِلَّا هُوَ مَعَهُمْ أَيْنَ مَا كَانُوا ۖ ثُمَّ يُنَبِّئُهُمْ بِمَا عَمِلُوا يَوْمَ الْقِيَامَةِ ۚ إِنَّ اللَّهَ بِكُلِّ شَيْءٍ عَلِيمٌ', NULL, 6, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_8', 'quran58verse8', 'Quran 58:8', 'quran-58-verse-8', '(Original Book)', 'ara', 'أَلَمْ تَرَ إِلَى الَّذِينَ نُهُوا عَنِ النَّجْوَىٰ ثُمَّ يَعُودُونَ لِمَا نُهُوا عَنْهُ وَيَتَنَاجَوْنَ بِالْإِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُولِ وَإِذَا جَاءُوكَ حَيَّوْكَ بِمَا لَمْ يُحَيِّكَ بِهِ اللَّهُ وَيَقُولُونَ فِي أَنْفُسِهِمْ لَوْلَا يُعَذِّبُنَا اللَّهُ بِمَا نَقُولُ ۚ حَسْبُهُمْ جَهَنَّمُ يَصْلَوْنَهَا ۖ فَبِئْسَ الْمَصِيرُ', NULL, 7, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_9', 'quran58verse9', 'Quran 58:9', 'quran-58-verse-9', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا تَنَاجَيْتُمْ فَلَا تَتَنَاجَوْا بِالْإِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُولِ وَتَنَاجَوْا بِالْبِرِّ وَالتَّقْوَىٰ ۖ وَاتَّقُوا اللَّهَ الَّذِي إِلَيْهِ تُحْشَرُونَ', NULL, 8, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_10', 'quran58verse10', 'Quran 58:10', 'quran-58-verse-10', '(Original Book)', 'ara', 'إِنَّمَا النَّجْوَىٰ مِنَ الشَّيْطَانِ لِيَحْزُنَ الَّذِينَ آمَنُوا وَلَيْسَ بِضَارِّهِمْ شَيْئًا إِلَّا بِإِذْنِ اللَّهِ ۚ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', NULL, 9, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_11', 'quran58verse11', 'Quran 58:11', 'quran-58-verse-11', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا قِيلَ لَكُمْ تَفَسَّحُوا فِي الْمَجَالِسِ فَافْسَحُوا يَفْسَحِ اللَّهُ لَكُمْ ۖ وَإِذَا قِيلَ انْشُزُوا فَانْشُزُوا يَرْفَعِ اللَّهُ الَّذِينَ آمَنُوا مِنْكُمْ وَالَّذِينَ أُوتُوا الْعِلْمَ دَرَجَاتٍ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', NULL, 10, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_12', 'quran58verse12', 'Quran 58:12', 'quran-58-verse-12', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا نَاجَيْتُمُ الرَّسُولَ فَقَدِّمُوا بَيْنَ يَدَيْ نَجْوَاكُمْ صَدَقَةً ۚ ذَٰلِكَ خَيْرٌ لَكُمْ وَأَطْهَرُ ۚ فَإِنْ لَمْ تَجِدُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', NULL, 11, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_13', 'quran58verse13', 'Quran 58:13', 'quran-58-verse-13', '(Original Book)', 'ara', 'أَأَشْفَقْتُمْ أَنْ تُقَدِّمُوا بَيْنَ يَدَيْ نَجْوَاكُمْ صَدَقَاتٍ ۚ فَإِذْ لَمْ تَفْعَلُوا وَتَابَ اللَّهُ عَلَيْكُمْ فَأَقِيمُوا الصَّلَاةَ وَآتُوا الزَّكَاةَ وَأَطِيعُوا اللَّهَ وَرَسُولَهُ ۚ وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ', NULL, 12, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_14', 'quran58verse14', 'Quran 58:14', 'quran-58-verse-14', '(Original Book)', 'ara', 'أَلَمْ تَرَ إِلَى الَّذِينَ تَوَلَّوْا قَوْمًا غَضِبَ اللَّهُ عَلَيْهِمْ مَا هُمْ مِنْكُمْ وَلَا مِنْهُمْ وَيَحْلِفُونَ عَلَى الْكَذِبِ وَهُمْ يَعْلَمُونَ', NULL, 13, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_15', 'quran58verse15', 'Quran 58:15', 'quran-58-verse-15', '(Original Book)', 'ara', 'أَعَدَّ اللَّهُ لَهُمْ عَذَابًا شَدِيدًا ۖ إِنَّهُمْ سَاءَ مَا كَانُوا يَعْمَلُونَ', NULL, 14, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_16', 'quran58verse16', 'Quran 58:16', 'quran-58-verse-16', '(Original Book)', 'ara', 'اتَّخَذُوا أَيْمَانَهُمْ جُنَّةً فَصَدُّوا عَنْ سَبِيلِ اللَّهِ فَلَهُمْ عَذَابٌ مُهِينٌ', NULL, 15, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_24', 'quran67verse24', 'Quran 67:24', 'quran-67-verse-24', '(Original Book)', 'ara', 'قُلْ هُوَ الَّذِي ذَرَأَكُمْ فِي الْأَرْضِ وَإِلَيْهِ تُحْشَرُونَ', NULL, 23, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_17', 'quran58verse17', 'Quran 58:17', 'quran-58-verse-17', '(Original Book)', 'ara', 'لَنْ تُغْنِيَ عَنْهُمْ أَمْوَالُهُمْ وَلَا أَوْلَادُهُمْ مِنَ اللَّهِ شَيْئًا ۚ أُولَٰئِكَ أَصْحَابُ النَّارِ ۖ هُمْ فِيهَا خَالِدُونَ', NULL, 16, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_18', 'quran58verse18', 'Quran 58:18', 'quran-58-verse-18', '(Original Book)', 'ara', 'يَوْمَ يَبْعَثُهُمُ اللَّهُ جَمِيعًا فَيَحْلِفُونَ لَهُ كَمَا يَحْلِفُونَ لَكُمْ ۖ وَيَحْسَبُونَ أَنَّهُمْ عَلَىٰ شَيْءٍ ۚ أَلَا إِنَّهُمْ هُمُ الْكَاذِبُونَ', NULL, 17, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_19', 'quran58verse19', 'Quran 58:19', 'quran-58-verse-19', '(Original Book)', 'ara', 'اسْتَحْوَذَ عَلَيْهِمُ الشَّيْطَانُ فَأَنْسَاهُمْ ذِكْرَ اللَّهِ ۚ أُولَٰئِكَ حِزْبُ الشَّيْطَانِ ۚ أَلَا إِنَّ حِزْبَ الشَّيْطَانِ هُمُ الْخَاسِرُونَ', NULL, 18, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_20', 'quran58verse20', 'Quran 58:20', 'quran-58-verse-20', '(Original Book)', 'ara', 'إِنَّ الَّذِينَ يُحَادُّونَ اللَّهَ وَرَسُولَهُ أُولَٰئِكَ فِي الْأَذَلِّينَ', NULL, 19, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_21', 'quran58verse21', 'Quran 58:21', 'quran-58-verse-21', '(Original Book)', 'ara', 'كَتَبَ اللَّهُ لَأَغْلِبَنَّ أَنَا وَرُسُلِي ۚ إِنَّ اللَّهَ قَوِيٌّ عَزِيزٌ', NULL, 20, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_58_verse_22', 'quran58verse22', 'Quran 58:22', 'quran-58-verse-22', '(Original Book)', 'ara', 'لَا تَجِدُ قَوْمًا يُؤْمِنُونَ بِاللَّهِ وَالْيَوْمِ الْآخِرِ يُوَادُّونَ مَنْ حَادَّ اللَّهَ وَرَسُولَهُ وَلَوْ كَانُوا آبَاءَهُمْ أَوْ أَبْنَاءَهُمْ أَوْ إِخْوَانَهُمْ أَوْ عَشِيرَتَهُمْ ۚ أُولَٰئِكَ كَتَبَ فِي قُلُوبِهِمُ الْإِيمَانَ وَأَيَّدَهُمْ بِرُوحٍ مِنْهُ ۖ وَيُدْخِلُهُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا ۚ رَضِيَ اللَّهُ عَنْهُمْ وَرَضُوا عَنْهُ ۚ أُولَٰئِكَ حِزْبُ اللَّهِ ۚ أَلَا إِنَّ حِزْبَ اللَّهِ هُمُ الْمُفْلِحُونَ', NULL, 21, 'quran_58');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_1', 'quran59verse1', 'Quran 59:1', 'quran-59-verse-1', '(Original Book)', 'ara', 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۖ وَهُوَ الْعَزِيزُ الْحَكِيمُ', NULL, 0, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_2', 'quran59verse2', 'Quran 59:2', 'quran-59-verse-2', '(Original Book)', 'ara', 'هُوَ الَّذِي أَخْرَجَ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ مِنْ دِيَارِهِمْ لِأَوَّلِ الْحَشْرِ ۚ مَا ظَنَنْتُمْ أَنْ يَخْرُجُوا ۖ وَظَنُّوا أَنَّهُمْ مَانِعَتُهُمْ حُصُونُهُمْ مِنَ اللَّهِ فَأَتَاهُمُ اللَّهُ مِنْ حَيْثُ لَمْ يَحْتَسِبُوا ۖ وَقَذَفَ فِي قُلُوبِهِمُ الرُّعْبَ ۚ يُخْرِبُونَ بُيُوتَهُمْ بِأَيْدِيهِمْ وَأَيْدِي الْمُؤْمِنِينَ فَاعْتَبِرُوا يَا أُولِي الْأَبْصَارِ', NULL, 1, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_3', 'quran59verse3', 'Quran 59:3', 'quran-59-verse-3', '(Original Book)', 'ara', 'وَلَوْلَا أَنْ كَتَبَ اللَّهُ عَلَيْهِمُ الْجَلَاءَ لَعَذَّبَهُمْ فِي الدُّنْيَا ۖ وَلَهُمْ فِي الْآخِرَةِ عَذَابُ النَّارِ', NULL, 2, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_4', 'quran59verse4', 'Quran 59:4', 'quran-59-verse-4', '(Original Book)', 'ara', 'ذَٰلِكَ بِأَنَّهُمْ شَاقُّوا اللَّهَ وَرَسُولَهُ ۖ وَمَنْ يُشَاقِّ اللَّهَ فَإِنَّ اللَّهَ شَدِيدُ الْعِقَابِ', NULL, 3, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_5', 'quran59verse5', 'Quran 59:5', 'quran-59-verse-5', '(Original Book)', 'ara', 'مَا قَطَعْتُمْ مِنْ لِينَةٍ أَوْ تَرَكْتُمُوهَا قَائِمَةً عَلَىٰ أُصُولِهَا فَبِإِذْنِ اللَّهِ وَلِيُخْزِيَ الْفَاسِقِينَ', NULL, 4, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_6', 'quran59verse6', 'Quran 59:6', 'quran-59-verse-6', '(Original Book)', 'ara', 'وَمَا أَفَاءَ اللَّهُ عَلَىٰ رَسُولِهِ مِنْهُمْ فَمَا أَوْجَفْتُمْ عَلَيْهِ مِنْ خَيْلٍ وَلَا رِكَابٍ وَلَٰكِنَّ اللَّهَ يُسَلِّطُ رُسُلَهُ عَلَىٰ مَنْ يَشَاءُ ۚ وَاللَّهُ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', NULL, 5, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_7', 'quran59verse7', 'Quran 59:7', 'quran-59-verse-7', '(Original Book)', 'ara', 'مَا أَفَاءَ اللَّهُ عَلَىٰ رَسُولِهِ مِنْ أَهْلِ الْقُرَىٰ فَلِلَّهِ وَلِلرَّسُولِ وَلِذِي الْقُرْبَىٰ وَالْيَتَامَىٰ وَالْمَسَاكِينِ وَابْنِ السَّبِيلِ كَيْ لَا يَكُونَ دُولَةً بَيْنَ الْأَغْنِيَاءِ مِنْكُمْ ۚ وَمَا آتَاكُمُ الرَّسُولُ فَخُذُوهُ وَمَا نَهَاكُمْ عَنْهُ فَانْتَهُوا ۚ وَاتَّقُوا اللَّهَ ۖ إِنَّ اللَّهَ شَدِيدُ الْعِقَابِ', NULL, 6, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_8', 'quran59verse8', 'Quran 59:8', 'quran-59-verse-8', '(Original Book)', 'ara', 'لِلْفُقَرَاءِ الْمُهَاجِرِينَ الَّذِينَ أُخْرِجُوا مِنْ دِيَارِهِمْ وَأَمْوَالِهِمْ يَبْتَغُونَ فَضْلًا مِنَ اللَّهِ وَرِضْوَانًا وَيَنْصُرُونَ اللَّهَ وَرَسُولَهُ ۚ أُولَٰئِكَ هُمُ الصَّادِقُونَ', NULL, 7, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_9', 'quran59verse9', 'Quran 59:9', 'quran-59-verse-9', '(Original Book)', 'ara', 'وَالَّذِينَ تَبَوَّءُوا الدَّارَ وَالْإِيمَانَ مِنْ قَبْلِهِمْ يُحِبُّونَ مَنْ هَاجَرَ إِلَيْهِمْ وَلَا يَجِدُونَ فِي صُدُورِهِمْ حَاجَةً مِمَّا أُوتُوا وَيُؤْثِرُونَ عَلَىٰ أَنْفُسِهِمْ وَلَوْ كَانَ بِهِمْ خَصَاصَةٌ ۚ وَمَنْ يُوقَ شُحَّ نَفْسِهِ فَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ', NULL, 8, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_62_verse_11', 'quran62verse11', 'Quran 62:11', 'quran-62-verse-11', '(Original Book)', 'ara', 'وَإِذَا رَأَوْا تِجَارَةً أَوْ لَهْوًا انْفَضُّوا إِلَيْهَا وَتَرَكُوكَ قَائِمًا ۚ قُلْ مَا عِنْدَ اللَّهِ خَيْرٌ مِنَ اللَّهْوِ وَمِنَ التِّجَارَةِ ۚ وَاللَّهُ خَيْرُ الرَّازِقِينَ', NULL, 10, 'quran_62');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_10', 'quran59verse10', 'Quran 59:10', 'quran-59-verse-10', '(Original Book)', 'ara', 'وَالَّذِينَ جَاءُوا مِنْ بَعْدِهِمْ يَقُولُونَ رَبَّنَا اغْفِرْ لَنَا وَلِإِخْوَانِنَا الَّذِينَ سَبَقُونَا بِالْإِيمَانِ وَلَا تَجْعَلْ فِي قُلُوبِنَا غِلًّا لِلَّذِينَ آمَنُوا رَبَّنَا إِنَّكَ رَءُوفٌ رَحِيمٌ', NULL, 9, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_11', 'quran59verse11', 'Quran 59:11', 'quran-59-verse-11', '(Original Book)', 'ara', 'أَلَمْ تَرَ إِلَى الَّذِينَ نَافَقُوا يَقُولُونَ لِإِخْوَانِهِمُ الَّذِينَ كَفَرُوا مِنْ أَهْلِ الْكِتَابِ لَئِنْ أُخْرِجْتُمْ لَنَخْرُجَنَّ مَعَكُمْ وَلَا نُطِيعُ فِيكُمْ أَحَدًا أَبَدًا وَإِنْ قُوتِلْتُمْ لَنَنْصُرَنَّكُمْ وَاللَّهُ يَشْهَدُ إِنَّهُمْ لَكَاذِبُونَ', NULL, 10, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_12', 'quran59verse12', 'Quran 59:12', 'quran-59-verse-12', '(Original Book)', 'ara', 'لَئِنْ أُخْرِجُوا لَا يَخْرُجُونَ مَعَهُمْ وَلَئِنْ قُوتِلُوا لَا يَنْصُرُونَهُمْ وَلَئِنْ نَصَرُوهُمْ لَيُوَلُّنَّ الْأَدْبَارَ ثُمَّ لَا يُنْصَرُونَ', NULL, 11, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_13', 'quran59verse13', 'Quran 59:13', 'quran-59-verse-13', '(Original Book)', 'ara', 'لَأَنْتُمْ أَشَدُّ رَهْبَةً فِي صُدُورِهِمْ مِنَ اللَّهِ ۚ ذَٰلِكَ بِأَنَّهُمْ قَوْمٌ لَا يَفْقَهُونَ', NULL, 12, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_14', 'quran59verse14', 'Quran 59:14', 'quran-59-verse-14', '(Original Book)', 'ara', 'لَا يُقَاتِلُونَكُمْ جَمِيعًا إِلَّا فِي قُرًى مُحَصَّنَةٍ أَوْ مِنْ وَرَاءِ جُدُرٍ ۚ بَأْسُهُمْ بَيْنَهُمْ شَدِيدٌ ۚ تَحْسَبُهُمْ جَمِيعًا وَقُلُوبُهُمْ شَتَّىٰ ۚ ذَٰلِكَ بِأَنَّهُمْ قَوْمٌ لَا يَعْقِلُونَ', NULL, 13, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_15', 'quran59verse15', 'Quran 59:15', 'quran-59-verse-15', '(Original Book)', 'ara', 'كَمَثَلِ الَّذِينَ مِنْ قَبْلِهِمْ قَرِيبًا ۖ ذَاقُوا وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ', NULL, 14, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_16', 'quran59verse16', 'Quran 59:16', 'quran-59-verse-16', '(Original Book)', 'ara', 'كَمَثَلِ الشَّيْطَانِ إِذْ قَالَ لِلْإِنْسَانِ اكْفُرْ فَلَمَّا كَفَرَ قَالَ إِنِّي بَرِيءٌ مِنْكَ إِنِّي أَخَافُ اللَّهَ رَبَّ الْعَالَمِينَ', NULL, 15, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_17', 'quran59verse17', 'Quran 59:17', 'quran-59-verse-17', '(Original Book)', 'ara', 'فَكَانَ عَاقِبَتَهُمَا أَنَّهُمَا فِي النَّارِ خَالِدَيْنِ فِيهَا ۚ وَذَٰلِكَ جَزَاءُ الظَّالِمِينَ', NULL, 16, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_18', 'quran59verse18', 'Quran 59:18', 'quran-59-verse-18', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا اتَّقُوا اللَّهَ وَلْتَنْظُرْ نَفْسٌ مَا قَدَّمَتْ لِغَدٍ ۖ وَاتَّقُوا اللَّهَ ۚ إِنَّ اللَّهَ خَبِيرٌ بِمَا تَعْمَلُونَ', NULL, 17, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_19', 'quran59verse19', 'Quran 59:19', 'quran-59-verse-19', '(Original Book)', 'ara', 'وَلَا تَكُونُوا كَالَّذِينَ نَسُوا اللَّهَ فَأَنْسَاهُمْ أَنْفُسَهُمْ ۚ أُولَٰئِكَ هُمُ الْفَاسِقُونَ', NULL, 18, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_20', 'quran59verse20', 'Quran 59:20', 'quran-59-verse-20', '(Original Book)', 'ara', 'لَا يَسْتَوِي أَصْحَابُ النَّارِ وَأَصْحَابُ الْجَنَّةِ ۚ أَصْحَابُ الْجَنَّةِ هُمُ الْفَائِزُونَ', NULL, 19, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_21', 'quran59verse21', 'Quran 59:21', 'quran-59-verse-21', '(Original Book)', 'ara', 'لَوْ أَنْزَلْنَا هَٰذَا الْقُرْآنَ عَلَىٰ جَبَلٍ لَرَأَيْتَهُ خَاشِعًا مُتَصَدِّعًا مِنْ خَشْيَةِ اللَّهِ ۚ وَتِلْكَ الْأَمْثَالُ نَضْرِبُهَا لِلنَّاسِ لَعَلَّهُمْ يَتَفَكَّرُونَ', NULL, 20, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_22', 'quran59verse22', 'Quran 59:22', 'quran-59-verse-22', '(Original Book)', 'ara', 'هُوَ اللَّهُ الَّذِي لَا إِلَٰهَ إِلَّا هُوَ ۖ عَالِمُ الْغَيْبِ وَالشَّهَادَةِ ۖ هُوَ الرَّحْمَٰنُ الرَّحِيمُ', NULL, 21, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_23', 'quran59verse23', 'Quran 59:23', 'quran-59-verse-23', '(Original Book)', 'ara', 'هُوَ اللَّهُ الَّذِي لَا إِلَٰهَ إِلَّا هُوَ الْمَلِكُ الْقُدُّوسُ السَّلَامُ الْمُؤْمِنُ الْمُهَيْمِنُ الْعَزِيزُ الْجَبَّارُ الْمُتَكَبِّرُ ۚ سُبْحَانَ اللَّهِ عَمَّا يُشْرِكُونَ', NULL, 22, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_59_verse_24', 'quran59verse24', 'Quran 59:24', 'quran-59-verse-24', '(Original Book)', 'ara', 'هُوَ اللَّهُ الْخَالِقُ الْبَارِئُ الْمُصَوِّرُ ۖ لَهُ الْأَسْمَاءُ الْحُسْنَىٰ ۚ يُسَبِّحُ لَهُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ ۖ وَهُوَ الْعَزِيزُ الْحَكِيمُ', NULL, 23, 'quran_59');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_60_verse_1', 'quran60verse1', 'Quran 60:1', 'quran-60-verse-1', '(Original Book)', 'ara', 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَّخِذُوا عَدُوِّي وَعَدُوَّكُمْ أَوْلِيَاءَ تُلْقُونَ إِلَيْهِمْ بِالْمَوَدَّةِ وَقَدْ كَفَرُوا بِمَا جَاءَكُمْ مِنَ الْحَقِّ يُخْرِجُونَ الرَّسُولَ وَإِيَّاكُمْ ۙ أَنْ تُؤْمِنُوا بِاللَّهِ رَبِّكُمْ إِنْ كُنْتُمْ خَرَجْتُمْ جِهَادًا فِي سَبِيلِي وَابْتِغَاءَ مَرْضَاتِي ۚ تُسِرُّونَ إِلَيْهِمْ بِالْمَوَدَّةِ وَأَنَا أَعْلَمُ بِمَا أَخْفَيْتُمْ وَمَا أَعْلَنْتُمْ ۚ وَمَنْ يَفْعَلْهُ مِنْكُمْ فَقَدْ ضَلَّ سَوَاءَ السَّبِيلِ', NULL, 0, 'quran_60');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_60_verse_2', 'quran60verse2', 'Quran 60:2', 'quran-60-verse-2', '(Original Book)', 'ara', 'إِنْ يَثْقَفُوكُمْ يَكُونُوا لَكُمْ أَعْدَاءً وَيَبْسُطُوا إِلَيْكُمْ أَيْدِيَهُمْ وَأَلْسِنَتَهُمْ بِالسُّوءِ وَوَدُّوا لَوْ تَكْفُرُونَ', NULL, 1, 'quran_60');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_25', 'quran67verse25', 'Quran 67:25', 'quran-67-verse-25', '(Original Book)', 'ara', 'وَيَقُولُونَ مَتَىٰ هَٰذَا الْوَعْدُ إِنْ كُنْتُمْ صَادِقِينَ', NULL, 24, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_60_verse_4', 'quran60verse4', 'Quran 60:4', 'quran-60-verse-4', '(Original Book)', 'ara', 'قَدْ كَانَتْ لَكُمْ أُسْوَةٌ حَسَنَةٌ فِي إِبْرَاهِيمَ وَالَّذِينَ مَعَهُ إِذْ قَالُوا لِقَوْمِهِمْ إِنَّا بُرَآءُ مِنْكُمْ وَمِمَّا تَعْبُدُونَ مِنْ دُونِ اللَّهِ كَفَرْنَا بِكُمْ وَبَدَا بَيْنَنَا وَبَيْنَكُمُ الْعَدَاوَةُ وَالْبَغْضَاءُ أَبَدًا حَتَّىٰ تُؤْمِنُوا بِاللَّهِ وَحْدَهُ إِلَّا قَوْلَ إِبْرَاهِيمَ لِأَبِيهِ لَأَسْتَغْفِرَنَّ لَكَ وَمَا أَمْلِكُ لَكَ مِنَ اللَّهِ مِنْ شَيْءٍ ۖ رَبَّنَا عَلَيْكَ تَوَكَّلْنَا وَإِلَيْكَ أَنَبْنَا وَإِلَيْكَ الْمَصِيرُ', NULL, 3, 'quran_60');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_60_verse_5', 'quran60verse5', 'Quran 60:5', 'quran-60-verse-5', '(Original Book)', 'ara', 'رَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِلَّذِينَ كَفَرُوا وَاغْفِرْ لَنَا رَبَّنَا ۖ إِنَّكَ أَنْتَ الْعَزِيزُ الْحَكِيمُ', NULL, 4, 'quran_60');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_60_verse_6', 'quran60verse6', 'Quran 60:6', 'quran-60-verse-6', '(Original Book)', 'ara', 'لَقَدْ كَانَ لَكُمْ فِيهِمْ أُسْوَةٌ حَسَنَةٌ لِمَنْ كَانَ يَرْجُو اللَّهَ وَالْيَوْمَ الْآخِرَ ۚ وَمَنْ يَتَوَلَّ فَإِنَّ اللَّهَ هُوَ الْغَنِيُّ الْحَمِيدُ', NULL, 5, 'quran_60');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_60_verse_7', 'quran60verse7', 'Quran 60:7', 'quran-60-verse-7', '(Original Book)', 'ara', 'عَسَى اللَّهُ أَنْ يَجْعَلَ بَيْنَكُمْ وَبَيْنَ الَّذِينَ عَادَيْتُمْ مِنْهُمْ مَوَدَّةً ۚ وَاللَّهُ قَدِيرٌ ۚ وَاللَّهُ غَفُورٌ رَحِيمٌ', NULL, 6, 'quran_60');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_60_verse_8', 'quran60verse8', 'Quran 60:8', 'quran-60-verse-8', '(Original Book)', 'ara', 'لَا يَنْهَاكُمُ اللَّهُ عَنِ الَّذِينَ لَمْ يُقَاتِلُوكُمْ فِي الدِّينِ وَلَمْ يُخْرِجُوكُمْ مِنْ دِيَارِكُمْ أَنْ تَبَرُّوهُمْ وَتُقْسِطُوا إِلَيْهِمْ ۚ إِنَّ اللَّهَ يُحِبُّ الْمُقْسِطِينَ', NULL, 7, 'quran_60');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_60_verse_9', 'quran60verse9', 'Quran 60:9', 'quran-60-verse-9', '(Original Book)', 'ara', 'إِنَّمَا يَنْهَاكُمُ اللَّهُ عَنِ الَّذِينَ قَاتَلُوكُمْ فِي الدِّينِ وَأَخْرَجُوكُمْ مِنْ دِيَارِكُمْ وَظَاهَرُوا عَلَىٰ إِخْرَاجِكُمْ أَنْ تَوَلَّوْهُمْ ۚ وَمَنْ يَتَوَلَّهُمْ فَأُولَٰئِكَ هُمُ الظَّالِمُونَ', NULL, 8, 'quran_60');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_60_verse_10', 'quran60verse10', 'Quran 60:10', 'quran-60-verse-10', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا جَاءَكُمُ الْمُؤْمِنَاتُ مُهَاجِرَاتٍ فَامْتَحِنُوهُنَّ ۖ اللَّهُ أَعْلَمُ بِإِيمَانِهِنَّ ۖ فَإِنْ عَلِمْتُمُوهُنَّ مُؤْمِنَاتٍ فَلَا تَرْجِعُوهُنَّ إِلَى الْكُفَّارِ ۖ لَا هُنَّ حِلٌّ لَهُمْ وَلَا هُمْ يَحِلُّونَ لَهُنَّ ۖ وَآتُوهُمْ مَا أَنْفَقُوا ۚ وَلَا جُنَاحَ عَلَيْكُمْ أَنْ تَنْكِحُوهُنَّ إِذَا آتَيْتُمُوهُنَّ أُجُورَهُنَّ ۚ وَلَا تُمْسِكُوا بِعِصَمِ الْكَوَافِرِ وَاسْأَلُوا مَا أَنْفَقْتُمْ وَلْيَسْأَلُوا مَا أَنْفَقُوا ۚ ذَٰلِكُمْ حُكْمُ اللَّهِ ۖ يَحْكُمُ بَيْنَكُمْ ۚ وَاللَّهُ عَلِيمٌ حَكِيمٌ', NULL, 9, 'quran_60');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_60_verse_11', 'quran60verse11', 'Quran 60:11', 'quran-60-verse-11', '(Original Book)', 'ara', 'وَإِنْ فَاتَكُمْ شَيْءٌ مِنْ أَزْوَاجِكُمْ إِلَى الْكُفَّارِ فَعَاقَبْتُمْ فَآتُوا الَّذِينَ ذَهَبَتْ أَزْوَاجُهُمْ مِثْلَ مَا أَنْفَقُوا ۚ وَاتَّقُوا اللَّهَ الَّذِي أَنْتُمْ بِهِ مُؤْمِنُونَ', NULL, 10, 'quran_60');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_60_verse_12', 'quran60verse12', 'Quran 60:12', 'quran-60-verse-12', '(Original Book)', 'ara', 'يَا أَيُّهَا النَّبِيُّ إِذَا جَاءَكَ الْمُؤْمِنَاتُ يُبَايِعْنَكَ عَلَىٰ أَنْ لَا يُشْرِكْنَ بِاللَّهِ شَيْئًا وَلَا يَسْرِقْنَ وَلَا يَزْنِينَ وَلَا يَقْتُلْنَ أَوْلَادَهُنَّ وَلَا يَأْتِينَ بِبُهْتَانٍ يَفْتَرِينَهُ بَيْنَ أَيْدِيهِنَّ وَأَرْجُلِهِنَّ وَلَا يَعْصِينَكَ فِي مَعْرُوفٍ ۙ فَبَايِعْهُنَّ وَاسْتَغْفِرْ لَهُنَّ اللَّهَ ۖ إِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', NULL, 11, 'quran_60');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_60_verse_13', 'quran60verse13', 'Quran 60:13', 'quran-60-verse-13', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تَتَوَلَّوْا قَوْمًا غَضِبَ اللَّهُ عَلَيْهِمْ قَدْ يَئِسُوا مِنَ الْآخِرَةِ كَمَا يَئِسَ الْكُفَّارُ مِنْ أَصْحَابِ الْقُبُورِ', NULL, 12, 'quran_60');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_61_verse_1', 'quran61verse1', 'Quran 61:1', 'quran-61-verse-1', '(Original Book)', 'ara', 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ سَبَّحَ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۖ وَهُوَ الْعَزِيزُ الْحَكِيمُ', NULL, 0, 'quran_61');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_61_verse_2', 'quran61verse2', 'Quran 61:2', 'quran-61-verse-2', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا لِمَ تَقُولُونَ مَا لَا تَفْعَلُونَ', NULL, 1, 'quran_61');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_61_verse_3', 'quran61verse3', 'Quran 61:3', 'quran-61-verse-3', '(Original Book)', 'ara', 'كَبُرَ مَقْتًا عِنْدَ اللَّهِ أَنْ تَقُولُوا مَا لَا تَفْعَلُونَ', NULL, 2, 'quran_61');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_61_verse_4', 'quran61verse4', 'Quran 61:4', 'quran-61-verse-4', '(Original Book)', 'ara', 'إِنَّ اللَّهَ يُحِبُّ الَّذِينَ يُقَاتِلُونَ فِي سَبِيلِهِ صَفًّا كَأَنَّهُمْ بُنْيَانٌ مَرْصُوصٌ', NULL, 3, 'quran_61');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_61_verse_5', 'quran61verse5', 'Quran 61:5', 'quran-61-verse-5', '(Original Book)', 'ara', 'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِ يَا قَوْمِ لِمَ تُؤْذُونَنِي وَقَدْ تَعْلَمُونَ أَنِّي رَسُولُ اللَّهِ إِلَيْكُمْ ۖ فَلَمَّا زَاغُوا أَزَاغَ اللَّهُ قُلُوبَهُمْ ۚ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ', NULL, 4, 'quran_61');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_78_verse_5', 'quran78verse5', 'Quran 78:5', 'quran-78-verse-5', '(Original Book)', 'ara', 'ثُمَّ كَلَّا سَيَعْلَمُونَ', NULL, 4, 'quran_78');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_61_verse_6', 'quran61verse6', 'Quran 61:6', 'quran-61-verse-6', '(Original Book)', 'ara', 'وَإِذْ قَالَ عِيسَى ابْنُ مَرْيَمَ يَا بَنِي إِسْرَائِيلَ إِنِّي رَسُولُ اللَّهِ إِلَيْكُمْ مُصَدِّقًا لِمَا بَيْنَ يَدَيَّ مِنَ التَّوْرَاةِ وَمُبَشِّرًا بِرَسُولٍ يَأْتِي مِنْ بَعْدِي اسْمُهُ أَحْمَدُ ۖ فَلَمَّا جَاءَهُمْ بِالْبَيِّنَاتِ قَالُوا هَٰذَا سِحْرٌ مُبِينٌ', NULL, 5, 'quran_61');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_61_verse_7', 'quran61verse7', 'Quran 61:7', 'quran-61-verse-7', '(Original Book)', 'ara', 'وَمَنْ أَظْلَمُ مِمَّنِ افْتَرَىٰ عَلَى اللَّهِ الْكَذِبَ وَهُوَ يُدْعَىٰ إِلَى الْإِسْلَامِ ۚ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ', NULL, 6, 'quran_61');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_61_verse_8', 'quran61verse8', 'Quran 61:8', 'quran-61-verse-8', '(Original Book)', 'ara', 'يُرِيدُونَ لِيُطْفِئُوا نُورَ اللَّهِ بِأَفْوَاهِهِمْ وَاللَّهُ مُتِمُّ نُورِهِ وَلَوْ كَرِهَ الْكَافِرُونَ', NULL, 7, 'quran_61');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_61_verse_9', 'quran61verse9', 'Quran 61:9', 'quran-61-verse-9', '(Original Book)', 'ara', 'هُوَ الَّذِي أَرْسَلَ رَسُولَهُ بِالْهُدَىٰ وَدِينِ الْحَقِّ لِيُظْهِرَهُ عَلَى الدِّينِ كُلِّهِ وَلَوْ كَرِهَ الْمُشْرِكُونَ', NULL, 8, 'quran_61');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_61_verse_10', 'quran61verse10', 'Quran 61:10', 'quran-61-verse-10', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا هَلْ أَدُلُّكُمْ عَلَىٰ تِجَارَةٍ تُنْجِيكُمْ مِنْ عَذَابٍ أَلِيمٍ', NULL, 9, 'quran_61');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_61_verse_11', 'quran61verse11', 'Quran 61:11', 'quran-61-verse-11', '(Original Book)', 'ara', 'تُؤْمِنُونَ بِاللَّهِ وَرَسُولِهِ وَتُجَاهِدُونَ فِي سَبِيلِ اللَّهِ بِأَمْوَالِكُمْ وَأَنْفُسِكُمْ ۚ ذَٰلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ', NULL, 10, 'quran_61');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_61_verse_12', 'quran61verse12', 'Quran 61:12', 'quran-61-verse-12', '(Original Book)', 'ara', 'يَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَيُدْخِلْكُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ وَمَسَاكِنَ طَيِّبَةً فِي جَنَّاتِ عَدْنٍ ۚ ذَٰلِكَ الْفَوْزُ الْعَظِيمُ', NULL, 11, 'quran_61');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_61_verse_13', 'quran61verse13', 'Quran 61:13', 'quran-61-verse-13', '(Original Book)', 'ara', 'وَأُخْرَىٰ تُحِبُّونَهَا ۖ نَصْرٌ مِنَ اللَّهِ وَفَتْحٌ قَرِيبٌ ۗ وَبَشِّرِ الْمُؤْمِنِينَ', NULL, 12, 'quran_61');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_61_verse_14', 'quran61verse14', 'Quran 61:14', 'quran-61-verse-14', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا كُونُوا أَنْصَارَ اللَّهِ كَمَا قَالَ عِيسَى ابْنُ مَرْيَمَ لِلْحَوَارِيِّينَ مَنْ أَنْصَارِي إِلَى اللَّهِ ۖ قَالَ الْحَوَارِيُّونَ نَحْنُ أَنْصَارُ اللَّهِ ۖ فَآمَنَتْ طَائِفَةٌ مِنْ بَنِي إِسْرَائِيلَ وَكَفَرَتْ طَائِفَةٌ ۖ فَأَيَّدْنَا الَّذِينَ آمَنُوا عَلَىٰ عَدُوِّهِمْ فَأَصْبَحُوا ظَاهِرِينَ', NULL, 13, 'quran_61');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_62_verse_1', 'quran62verse1', 'Quran 62:1', 'quran-62-verse-1', '(Original Book)', 'ara', 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يُسَبِّحُ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ الْمَلِكِ الْقُدُّوسِ الْعَزِيزِ الْحَكِيمِ', NULL, 0, 'quran_62');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_62_verse_2', 'quran62verse2', 'Quran 62:2', 'quran-62-verse-2', '(Original Book)', 'ara', 'هُوَ الَّذِي بَعَثَ فِي الْأُمِّيِّينَ رَسُولًا مِنْهُمْ يَتْلُو عَلَيْهِمْ آيَاتِهِ وَيُزَكِّيهِمْ وَيُعَلِّمُهُمُ الْكِتَابَ وَالْحِكْمَةَ وَإِنْ كَانُوا مِنْ قَبْلُ لَفِي ضَلَالٍ مُبِينٍ', NULL, 1, 'quran_62');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_62_verse_3', 'quran62verse3', 'Quran 62:3', 'quran-62-verse-3', '(Original Book)', 'ara', 'وَآخَرِينَ مِنْهُمْ لَمَّا يَلْحَقُوا بِهِمْ ۚ وَهُوَ الْعَزِيزُ الْحَكِيمُ', NULL, 2, 'quran_62');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_62_verse_4', 'quran62verse4', 'Quran 62:4', 'quran-62-verse-4', '(Original Book)', 'ara', 'ذَٰلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ ۚ وَاللَّهُ ذُو الْفَضْلِ الْعَظِيمِ', NULL, 3, 'quran_62');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_62_verse_5', 'quran62verse5', 'Quran 62:5', 'quran-62-verse-5', '(Original Book)', 'ara', 'مَثَلُ الَّذِينَ حُمِّلُوا التَّوْرَاةَ ثُمَّ لَمْ يَحْمِلُوهَا كَمَثَلِ الْحِمَارِ يَحْمِلُ أَسْفَارًا ۚ بِئْسَ مَثَلُ الْقَوْمِ الَّذِينَ كَذَّبُوا بِآيَاتِ اللَّهِ ۚ وَاللَّهُ لَا يَهْدِي الْقَوْمَ الظَّالِمِينَ', NULL, 4, 'quran_62');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_62_verse_6', 'quran62verse6', 'Quran 62:6', 'quran-62-verse-6', '(Original Book)', 'ara', 'قُلْ يَا أَيُّهَا الَّذِينَ هَادُوا إِنْ زَعَمْتُمْ أَنَّكُمْ أَوْلِيَاءُ لِلَّهِ مِنْ دُونِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ إِنْ كُنْتُمْ صَادِقِينَ', NULL, 5, 'quran_62');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_62_verse_7', 'quran62verse7', 'Quran 62:7', 'quran-62-verse-7', '(Original Book)', 'ara', 'وَلَا يَتَمَنَّوْنَهُ أَبَدًا بِمَا قَدَّمَتْ أَيْدِيهِمْ ۚ وَاللَّهُ عَلِيمٌ بِالظَّالِمِينَ', NULL, 6, 'quran_62');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_62_verse_8', 'quran62verse8', 'Quran 62:8', 'quran-62-verse-8', '(Original Book)', 'ara', 'قُلْ إِنَّ الْمَوْتَ الَّذِي تَفِرُّونَ مِنْهُ فَإِنَّهُ مُلَاقِيكُمْ ۖ ثُمَّ تُرَدُّونَ إِلَىٰ عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُونَ', NULL, 7, 'quran_62');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_62_verse_9', 'quran62verse9', 'Quran 62:9', 'quran-62-verse-9', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا نُودِيَ لِلصَّلَاةِ مِنْ يَوْمِ الْجُمُعَةِ فَاسْعَوْا إِلَىٰ ذِكْرِ اللَّهِ وَذَرُوا الْبَيْعَ ۚ ذَٰلِكُمْ خَيْرٌ لَكُمْ إِنْ كُنْتُمْ تَعْلَمُونَ', NULL, 8, 'quran_62');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_62_verse_10', 'quran62verse10', 'Quran 62:10', 'quran-62-verse-10', '(Original Book)', 'ara', 'فَإِذَا قُضِيَتِ الصَّلَاةُ فَانْتَشِرُوا فِي الْأَرْضِ وَابْتَغُوا مِنْ فَضْلِ اللَّهِ وَاذْكُرُوا اللَّهَ كَثِيرًا لَعَلَّكُمْ تُفْلِحُونَ', NULL, 9, 'quran_62');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_63_verse_1', 'quran63verse1', 'Quran 63:1', 'quran-63-verse-1', '(Original Book)', 'ara', 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ إِذَا جَاءَكَ الْمُنَافِقُونَ قَالُوا نَشْهَدُ إِنَّكَ لَرَسُولُ اللَّهِ ۗ وَاللَّهُ يَعْلَمُ إِنَّكَ لَرَسُولُهُ وَاللَّهُ يَشْهَدُ إِنَّ الْمُنَافِقِينَ لَكَاذِبُونَ', NULL, 0, 'quran_63');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_63_verse_2', 'quran63verse2', 'Quran 63:2', 'quran-63-verse-2', '(Original Book)', 'ara', 'اتَّخَذُوا أَيْمَانَهُمْ جُنَّةً فَصَدُّوا عَنْ سَبِيلِ اللَّهِ ۚ إِنَّهُمْ سَاءَ مَا كَانُوا يَعْمَلُونَ', NULL, 1, 'quran_63');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_63_verse_3', 'quran63verse3', 'Quran 63:3', 'quran-63-verse-3', '(Original Book)', 'ara', 'ذَٰلِكَ بِأَنَّهُمْ آمَنُوا ثُمَّ كَفَرُوا فَطُبِعَ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَفْقَهُونَ', NULL, 2, 'quran_63');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_63_verse_4', 'quran63verse4', 'Quran 63:4', 'quran-63-verse-4', '(Original Book)', 'ara', 'وَإِذَا رَأَيْتَهُمْ تُعْجِبُكَ أَجْسَامُهُمْ ۖ وَإِنْ يَقُولُوا تَسْمَعْ لِقَوْلِهِمْ ۖ كَأَنَّهُمْ خُشُبٌ مُسَنَّدَةٌ ۖ يَحْسَبُونَ كُلَّ صَيْحَةٍ عَلَيْهِمْ ۚ هُمُ الْعَدُوُّ فَاحْذَرْهُمْ ۚ قَاتَلَهُمُ اللَّهُ ۖ أَنَّىٰ يُؤْفَكُونَ', NULL, 3, 'quran_63');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_63_verse_5', 'quran63verse5', 'Quran 63:5', 'quran-63-verse-5', '(Original Book)', 'ara', 'وَإِذَا قِيلَ لَهُمْ تَعَالَوْا يَسْتَغْفِرْ لَكُمْ رَسُولُ اللَّهِ لَوَّوْا رُءُوسَهُمْ وَرَأَيْتَهُمْ يَصُدُّونَ وَهُمْ مُسْتَكْبِرُونَ', NULL, 4, 'quran_63');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_63_verse_6', 'quran63verse6', 'Quran 63:6', 'quran-63-verse-6', '(Original Book)', 'ara', 'سَوَاءٌ عَلَيْهِمْ أَسْتَغْفَرْتَ لَهُمْ أَمْ لَمْ تَسْتَغْفِرْ لَهُمْ لَنْ يَغْفِرَ اللَّهُ لَهُمْ ۚ إِنَّ اللَّهَ لَا يَهْدِي الْقَوْمَ الْفَاسِقِينَ', NULL, 5, 'quran_63');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_63_verse_7', 'quran63verse7', 'Quran 63:7', 'quran-63-verse-7', '(Original Book)', 'ara', 'هُمُ الَّذِينَ يَقُولُونَ لَا تُنْفِقُوا عَلَىٰ مَنْ عِنْدَ رَسُولِ اللَّهِ حَتَّىٰ يَنْفَضُّوا ۗ وَلِلَّهِ خَزَائِنُ السَّمَاوَاتِ وَالْأَرْضِ وَلَٰكِنَّ الْمُنَافِقِينَ لَا يَفْقَهُونَ', NULL, 6, 'quran_63');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_63_verse_8', 'quran63verse8', 'Quran 63:8', 'quran-63-verse-8', '(Original Book)', 'ara', 'يَقُولُونَ لَئِنْ رَجَعْنَا إِلَى الْمَدِينَةِ لَيُخْرِجَنَّ الْأَعَزُّ مِنْهَا الْأَذَلَّ ۚ وَلِلَّهِ الْعِزَّةُ وَلِرَسُولِهِ وَلِلْمُؤْمِنِينَ وَلَٰكِنَّ الْمُنَافِقِينَ لَا يَعْلَمُونَ', NULL, 7, 'quran_63');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_63_verse_9', 'quran63verse9', 'Quran 63:9', 'quran-63-verse-9', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا لَا تُلْهِكُمْ أَمْوَالُكُمْ وَلَا أَوْلَادُكُمْ عَنْ ذِكْرِ اللَّهِ ۚ وَمَنْ يَفْعَلْ ذَٰلِكَ فَأُولَٰئِكَ هُمُ الْخَاسِرُونَ', NULL, 8, 'quran_63');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_63_verse_10', 'quran63verse10', 'Quran 63:10', 'quran-63-verse-10', '(Original Book)', 'ara', 'وَأَنْفِقُوا مِنْ مَا رَزَقْنَاكُمْ مِنْ قَبْلِ أَنْ يَأْتِيَ أَحَدَكُمُ الْمَوْتُ فَيَقُولَ رَبِّ لَوْلَا أَخَّرْتَنِي إِلَىٰ أَجَلٍ قَرِيبٍ فَأَصَّدَّقَ وَأَكُنْ مِنَ الصَّالِحِينَ', NULL, 9, 'quran_63');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_63_verse_11', 'quran63verse11', 'Quran 63:11', 'quran-63-verse-11', '(Original Book)', 'ara', 'وَلَنْ يُؤَخِّرَ اللَّهُ نَفْسًا إِذَا جَاءَ أَجَلُهَا ۚ وَاللَّهُ خَبِيرٌ بِمَا تَعْمَلُونَ', NULL, 10, 'quran_63');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_1', 'quran64verse1', 'Quran 64:1', 'quran-64-verse-1', '(Original Book)', 'ara', 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يُسَبِّحُ لِلَّهِ مَا فِي السَّمَاوَاتِ وَمَا فِي الْأَرْضِ ۖ لَهُ الْمُلْكُ وَلَهُ الْحَمْدُ ۖ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', NULL, 0, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_2', 'quran64verse2', 'Quran 64:2', 'quran-64-verse-2', '(Original Book)', 'ara', 'هُوَ الَّذِي خَلَقَكُمْ فَمِنْكُمْ كَافِرٌ وَمِنْكُمْ مُؤْمِنٌ ۚ وَاللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', NULL, 1, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_3', 'quran64verse3', 'Quran 64:3', 'quran-64-verse-3', '(Original Book)', 'ara', 'خَلَقَ السَّمَاوَاتِ وَالْأَرْضَ بِالْحَقِّ وَصَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْ ۖ وَإِلَيْهِ الْمَصِيرُ', NULL, 2, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_4', 'quran64verse4', 'Quran 64:4', 'quran-64-verse-4', '(Original Book)', 'ara', 'يَعْلَمُ مَا فِي السَّمَاوَاتِ وَالْأَرْضِ وَيَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَ ۚ وَاللَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ', NULL, 3, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_5', 'quran64verse5', 'Quran 64:5', 'quran-64-verse-5', '(Original Book)', 'ara', 'أَلَمْ يَأْتِكُمْ نَبَأُ الَّذِينَ كَفَرُوا مِنْ قَبْلُ فَذَاقُوا وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ', NULL, 4, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_6', 'quran64verse6', 'Quran 64:6', 'quran-64-verse-6', '(Original Book)', 'ara', 'ذَٰلِكَ بِأَنَّهُ كَانَتْ تَأْتِيهِمْ رُسُلُهُمْ بِالْبَيِّنَاتِ فَقَالُوا أَبَشَرٌ يَهْدُونَنَا فَكَفَرُوا وَتَوَلَّوْا ۚ وَاسْتَغْنَى اللَّهُ ۚ وَاللَّهُ غَنِيٌّ حَمِيدٌ', NULL, 5, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_7', 'quran64verse7', 'Quran 64:7', 'quran-64-verse-7', '(Original Book)', 'ara', 'زَعَمَ الَّذِينَ كَفَرُوا أَنْ لَنْ يُبْعَثُوا ۚ قُلْ بَلَىٰ وَرَبِّي لَتُبْعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلْتُمْ ۚ وَذَٰلِكَ عَلَى اللَّهِ يَسِيرٌ', NULL, 6, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_8', 'quran64verse8', 'Quran 64:8', 'quran-64-verse-8', '(Original Book)', 'ara', 'فَآمِنُوا بِاللَّهِ وَرَسُولِهِ وَالنُّورِ الَّذِي أَنْزَلْنَا ۚ وَاللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', NULL, 7, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_68_verse_30', 'quran68verse30', 'Quran 68:30', 'quran-68-verse-30', '(Original Book)', 'ara', 'فَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍ يَتَلَاوَمُونَ', NULL, 29, 'quran_68');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_9', 'quran64verse9', 'Quran 64:9', 'quran-64-verse-9', '(Original Book)', 'ara', 'يَوْمَ يَجْمَعُكُمْ لِيَوْمِ الْجَمْعِ ۖ ذَٰلِكَ يَوْمُ التَّغَابُنِ ۗ وَمَنْ يُؤْمِنْ بِاللَّهِ وَيَعْمَلْ صَالِحًا يُكَفِّرْ عَنْهُ سَيِّئَاتِهِ وَيُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۚ ذَٰلِكَ الْفَوْزُ الْعَظِيمُ', NULL, 8, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_10', 'quran64verse10', 'Quran 64:10', 'quran-64-verse-10', '(Original Book)', 'ara', 'وَالَّذِينَ كَفَرُوا وَكَذَّبُوا بِآيَاتِنَا أُولَٰئِكَ أَصْحَابُ النَّارِ خَالِدِينَ فِيهَا ۖ وَبِئْسَ الْمَصِيرُ', NULL, 9, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_11', 'quran64verse11', 'Quran 64:11', 'quran-64-verse-11', '(Original Book)', 'ara', 'مَا أَصَابَ مِنْ مُصِيبَةٍ إِلَّا بِإِذْنِ اللَّهِ ۗ وَمَنْ يُؤْمِنْ بِاللَّهِ يَهْدِ قَلْبَهُ ۚ وَاللَّهُ بِكُلِّ شَيْءٍ عَلِيمٌ', NULL, 10, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_12', 'quran64verse12', 'Quran 64:12', 'quran-64-verse-12', '(Original Book)', 'ara', 'وَأَطِيعُوا اللَّهَ وَأَطِيعُوا الرَّسُولَ ۚ فَإِنْ تَوَلَّيْتُمْ فَإِنَّمَا عَلَىٰ رَسُولِنَا الْبَلَاغُ الْمُبِينُ', NULL, 11, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_13', 'quran64verse13', 'Quran 64:13', 'quran-64-verse-13', '(Original Book)', 'ara', 'اللَّهُ لَا إِلَٰهَ إِلَّا هُوَ ۚ وَعَلَى اللَّهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُونَ', NULL, 12, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_14', 'quran64verse14', 'Quran 64:14', 'quran-64-verse-14', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا إِنَّ مِنْ أَزْوَاجِكُمْ وَأَوْلَادِكُمْ عَدُوًّا لَكُمْ فَاحْذَرُوهُمْ ۚ وَإِنْ تَعْفُوا وَتَصْفَحُوا وَتَغْفِرُوا فَإِنَّ اللَّهَ غَفُورٌ رَحِيمٌ', NULL, 13, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_15', 'quran64verse15', 'Quran 64:15', 'quran-64-verse-15', '(Original Book)', 'ara', 'إِنَّمَا أَمْوَالُكُمْ وَأَوْلَادُكُمْ فِتْنَةٌ ۚ وَاللَّهُ عِنْدَهُ أَجْرٌ عَظِيمٌ', NULL, 14, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_16', 'quran64verse16', 'Quran 64:16', 'quran-64-verse-16', '(Original Book)', 'ara', 'فَاتَّقُوا اللَّهَ مَا اسْتَطَعْتُمْ وَاسْمَعُوا وَأَطِيعُوا وَأَنْفِقُوا خَيْرًا لِأَنْفُسِكُمْ ۗ وَمَنْ يُوقَ شُحَّ نَفْسِهِ فَأُولَٰئِكَ هُمُ الْمُفْلِحُونَ', NULL, 15, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_17', 'quran64verse17', 'Quran 64:17', 'quran-64-verse-17', '(Original Book)', 'ara', 'إِنْ تُقْرِضُوا اللَّهَ قَرْضًا حَسَنًا يُضَاعِفْهُ لَكُمْ وَيَغْفِرْ لَكُمْ ۚ وَاللَّهُ شَكُورٌ حَلِيمٌ', NULL, 16, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_64_verse_18', 'quran64verse18', 'Quran 64:18', 'quran-64-verse-18', '(Original Book)', 'ara', 'عَالِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيزُ الْحَكِيمُ', NULL, 17, 'quran_64');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_65_verse_1', 'quran65verse1', 'Quran 65:1', 'quran-65-verse-1', '(Original Book)', 'ara', 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا النَّبِيُّ إِذَا طَلَّقْتُمُ النِّسَاءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ وَأَحْصُوا الْعِدَّةَ ۖ وَاتَّقُوا اللَّهَ رَبَّكُمْ ۖ لَا تُخْرِجُوهُنَّ مِنْ بُيُوتِهِنَّ وَلَا يَخْرُجْنَ إِلَّا أَنْ يَأْتِينَ بِفَاحِشَةٍ مُبَيِّنَةٍ ۚ وَتِلْكَ حُدُودُ اللَّهِ ۚ وَمَنْ يَتَعَدَّ حُدُودَ اللَّهِ فَقَدْ ظَلَمَ نَفْسَهُ ۚ لَا تَدْرِي لَعَلَّ اللَّهَ يُحْدِثُ بَعْدَ ذَٰلِكَ أَمْرًا', NULL, 0, 'quran_65');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_65_verse_2', 'quran65verse2', 'Quran 65:2', 'quran-65-verse-2', '(Original Book)', 'ara', 'فَإِذَا بَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ فَارِقُوهُنَّ بِمَعْرُوفٍ وَأَشْهِدُوا ذَوَيْ عَدْلٍ مِنْكُمْ وَأَقِيمُوا الشَّهَادَةَ لِلَّهِ ۚ ذَٰلِكُمْ يُوعَظُ بِهِ مَنْ كَانَ يُؤْمِنُ بِاللَّهِ وَالْيَوْمِ الْآخِرِ ۚ وَمَنْ يَتَّقِ اللَّهَ يَجْعَلْ لَهُ مَخْرَجًا', NULL, 1, 'quran_65');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_65_verse_3', 'quran65verse3', 'Quran 65:3', 'quran-65-verse-3', '(Original Book)', 'ara', 'وَيَرْزُقْهُ مِنْ حَيْثُ لَا يَحْتَسِبُ ۚ وَمَنْ يَتَوَكَّلْ عَلَى اللَّهِ فَهُوَ حَسْبُهُ ۚ إِنَّ اللَّهَ بَالِغُ أَمْرِهِ ۚ قَدْ جَعَلَ اللَّهُ لِكُلِّ شَيْءٍ قَدْرًا', NULL, 2, 'quran_65');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_65_verse_4', 'quran65verse4', 'Quran 65:4', 'quran-65-verse-4', '(Original Book)', 'ara', 'وَاللَّائِي يَئِسْنَ مِنَ الْمَحِيضِ مِنْ نِسَائِكُمْ إِنِ ارْتَبْتُمْ فَعِدَّتُهُنَّ ثَلَاثَةُ أَشْهُرٍ وَاللَّائِي لَمْ يَحِضْنَ ۚ وَأُولَاتُ الْأَحْمَالِ أَجَلُهُنَّ أَنْ يَضَعْنَ حَمْلَهُنَّ ۚ وَمَنْ يَتَّقِ اللَّهَ يَجْعَلْ لَهُ مِنْ أَمْرِهِ يُسْرًا', NULL, 3, 'quran_65');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_65_verse_5', 'quran65verse5', 'Quran 65:5', 'quran-65-verse-5', '(Original Book)', 'ara', 'ذَٰلِكَ أَمْرُ اللَّهِ أَنْزَلَهُ إِلَيْكُمْ ۚ وَمَنْ يَتَّقِ اللَّهَ يُكَفِّرْ عَنْهُ سَيِّئَاتِهِ وَيُعْظِمْ لَهُ أَجْرًا', NULL, 4, 'quran_65');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_65_verse_6', 'quran65verse6', 'Quran 65:6', 'quran-65-verse-6', '(Original Book)', 'ara', 'أَسْكِنُوهُنَّ مِنْ حَيْثُ سَكَنْتُمْ مِنْ وُجْدِكُمْ وَلَا تُضَارُّوهُنَّ لِتُضَيِّقُوا عَلَيْهِنَّ ۚ وَإِنْ كُنَّ أُولَاتِ حَمْلٍ فَأَنْفِقُوا عَلَيْهِنَّ حَتَّىٰ يَضَعْنَ حَمْلَهُنَّ ۚ فَإِنْ أَرْضَعْنَ لَكُمْ فَآتُوهُنَّ أُجُورَهُنَّ ۖ وَأْتَمِرُوا بَيْنَكُمْ بِمَعْرُوفٍ ۖ وَإِنْ تَعَاسَرْتُمْ فَسَتُرْضِعُ لَهُ أُخْرَىٰ', NULL, 5, 'quran_65');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_23', 'quran67verse23', 'Quran 67:23', 'quran-67-verse-23', '(Original Book)', 'ara', 'قُلْ هُوَ الَّذِي أَنْشَأَكُمْ وَجَعَلَ لَكُمُ السَّمْعَ وَالْأَبْصَارَ وَالْأَفْئِدَةَ ۖ قَلِيلًا مَا تَشْكُرُونَ', NULL, 22, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_65_verse_7', 'quran65verse7', 'Quran 65:7', 'quran-65-verse-7', '(Original Book)', 'ara', 'لِيُنْفِقْ ذُو سَعَةٍ مِنْ سَعَتِهِ ۖ وَمَنْ قُدِرَ عَلَيْهِ رِزْقُهُ فَلْيُنْفِقْ مِمَّا آتَاهُ اللَّهُ ۚ لَا يُكَلِّفُ اللَّهُ نَفْسًا إِلَّا مَا آتَاهَا ۚ سَيَجْعَلُ اللَّهُ بَعْدَ عُسْرٍ يُسْرًا', NULL, 6, 'quran_65');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_65_verse_8', 'quran65verse8', 'Quran 65:8', 'quran-65-verse-8', '(Original Book)', 'ara', 'وَكَأَيِّنْ مِنْ قَرْيَةٍ عَتَتْ عَنْ أَمْرِ رَبِّهَا وَرُسُلِهِ فَحَاسَبْنَاهَا حِسَابًا شَدِيدًا وَعَذَّبْنَاهَا عَذَابًا نُكْرًا', NULL, 7, 'quran_65');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_65_verse_9', 'quran65verse9', 'Quran 65:9', 'quran-65-verse-9', '(Original Book)', 'ara', 'فَذَاقَتْ وَبَالَ أَمْرِهَا وَكَانَ عَاقِبَةُ أَمْرِهَا خُسْرًا', NULL, 8, 'quran_65');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_65_verse_10', 'quran65verse10', 'Quran 65:10', 'quran-65-verse-10', '(Original Book)', 'ara', 'أَعَدَّ اللَّهُ لَهُمْ عَذَابًا شَدِيدًا ۖ فَاتَّقُوا اللَّهَ يَا أُولِي الْأَلْبَابِ الَّذِينَ آمَنُوا ۚ قَدْ أَنْزَلَ اللَّهُ إِلَيْكُمْ ذِكْرًا', NULL, 9, 'quran_65');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_65_verse_11', 'quran65verse11', 'Quran 65:11', 'quran-65-verse-11', '(Original Book)', 'ara', 'رَسُولًا يَتْلُو عَلَيْكُمْ آيَاتِ اللَّهِ مُبَيِّنَاتٍ لِيُخْرِجَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ مِنَ الظُّلُمَاتِ إِلَى النُّورِ ۚ وَمَنْ يُؤْمِنْ بِاللَّهِ وَيَعْمَلْ صَالِحًا يُدْخِلْهُ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ خَالِدِينَ فِيهَا أَبَدًا ۖ قَدْ أَحْسَنَ اللَّهُ لَهُ رِزْقًا', NULL, 10, 'quran_65');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_65_verse_12', 'quran65verse12', 'Quran 65:12', 'quran-65-verse-12', '(Original Book)', 'ara', 'اللَّهُ الَّذِي خَلَقَ سَبْعَ سَمَاوَاتٍ وَمِنَ الْأَرْضِ مِثْلَهُنَّ يَتَنَزَّلُ الْأَمْرُ بَيْنَهُنَّ لِتَعْلَمُوا أَنَّ اللَّهَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ وَأَنَّ اللَّهَ قَدْ أَحَاطَ بِكُلِّ شَيْءٍ عِلْمًا', NULL, 11, 'quran_65');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_66_verse_1', 'quran66verse1', 'Quran 66:1', 'quran-66-verse-1', '(Original Book)', 'ara', 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ يَا أَيُّهَا النَّبِيُّ لِمَ تُحَرِّمُ مَا أَحَلَّ اللَّهُ لَكَ ۖ تَبْتَغِي مَرْضَاتَ أَزْوَاجِكَ ۚ وَاللَّهُ غَفُورٌ رَحِيمٌ', NULL, 0, 'quran_66');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_66_verse_2', 'quran66verse2', 'Quran 66:2', 'quran-66-verse-2', '(Original Book)', 'ara', 'قَدْ فَرَضَ اللَّهُ لَكُمْ تَحِلَّةَ أَيْمَانِكُمْ ۚ وَاللَّهُ مَوْلَاكُمْ ۖ وَهُوَ الْعَلِيمُ الْحَكِيمُ', NULL, 1, 'quran_66');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_66_verse_3', 'quran66verse3', 'Quran 66:3', 'quran-66-verse-3', '(Original Book)', 'ara', 'وَإِذْ أَسَرَّ النَّبِيُّ إِلَىٰ بَعْضِ أَزْوَاجِهِ حَدِيثًا فَلَمَّا نَبَّأَتْ بِهِ وَأَظْهَرَهُ اللَّهُ عَلَيْهِ عَرَّفَ بَعْضَهُ وَأَعْرَضَ عَنْ بَعْضٍ ۖ فَلَمَّا نَبَّأَهَا بِهِ قَالَتْ مَنْ أَنْبَأَكَ هَٰذَا ۖ قَالَ نَبَّأَنِيَ الْعَلِيمُ الْخَبِيرُ', NULL, 2, 'quran_66');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_66_verse_4', 'quran66verse4', 'Quran 66:4', 'quran-66-verse-4', '(Original Book)', 'ara', 'إِنْ تَتُوبَا إِلَى اللَّهِ فَقَدْ صَغَتْ قُلُوبُكُمَا ۖ وَإِنْ تَظَاهَرَا عَلَيْهِ فَإِنَّ اللَّهَ هُوَ مَوْلَاهُ وَجِبْرِيلُ وَصَالِحُ الْمُؤْمِنِينَ ۖ وَالْمَلَائِكَةُ بَعْدَ ذَٰلِكَ ظَهِيرٌ', NULL, 3, 'quran_66');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_66_verse_5', 'quran66verse5', 'Quran 66:5', 'quran-66-verse-5', '(Original Book)', 'ara', 'عَسَىٰ رَبُّهُ إِنْ طَلَّقَكُنَّ أَنْ يُبْدِلَهُ أَزْوَاجًا خَيْرًا مِنْكُنَّ مُسْلِمَاتٍ مُؤْمِنَاتٍ قَانِتَاتٍ تَائِبَاتٍ عَابِدَاتٍ سَائِحَاتٍ ثَيِّبَاتٍ وَأَبْكَارًا', NULL, 4, 'quran_66');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_66_verse_6', 'quran66verse6', 'Quran 66:6', 'quran-66-verse-6', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا قُوا أَنْفُسَكُمْ وَأَهْلِيكُمْ نَارًا وَقُودُهَا النَّاسُ وَالْحِجَارَةُ عَلَيْهَا مَلَائِكَةٌ غِلَاظٌ شِدَادٌ لَا يَعْصُونَ اللَّهَ مَا أَمَرَهُمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ', NULL, 5, 'quran_66');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_66_verse_7', 'quran66verse7', 'Quran 66:7', 'quran-66-verse-7', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ كَفَرُوا لَا تَعْتَذِرُوا الْيَوْمَ ۖ إِنَّمَا تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُونَ', NULL, 6, 'quran_66');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_66_verse_8', 'quran66verse8', 'Quran 66:8', 'quran-66-verse-8', '(Original Book)', 'ara', 'يَا أَيُّهَا الَّذِينَ آمَنُوا تُوبُوا إِلَى اللَّهِ تَوْبَةً نَصُوحًا عَسَىٰ رَبُّكُمْ أَنْ يُكَفِّرَ عَنْكُمْ سَيِّئَاتِكُمْ وَيُدْخِلَكُمْ جَنَّاتٍ تَجْرِي مِنْ تَحْتِهَا الْأَنْهَارُ يَوْمَ لَا يُخْزِي اللَّهُ النَّبِيَّ وَالَّذِينَ آمَنُوا مَعَهُ ۖ نُورُهُمْ يَسْعَىٰ بَيْنَ أَيْدِيهِمْ وَبِأَيْمَانِهِمْ يَقُولُونَ رَبَّنَا أَتْمِمْ لَنَا نُورَنَا وَاغْفِرْ لَنَا ۖ إِنَّكَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', NULL, 7, 'quran_66');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_66_verse_9', 'quran66verse9', 'Quran 66:9', 'quran-66-verse-9', '(Original Book)', 'ara', 'يَا أَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنَافِقِينَ وَاغْلُظْ عَلَيْهِمْ ۚ وَمَأْوَاهُمْ جَهَنَّمُ ۖ وَبِئْسَ الْمَصِيرُ', NULL, 8, 'quran_66');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_66_verse_10', 'quran66verse10', 'Quran 66:10', 'quran-66-verse-10', '(Original Book)', 'ara', 'ضَرَبَ اللَّهُ مَثَلًا لِلَّذِينَ كَفَرُوا امْرَأَتَ نُوحٍ وَامْرَأَتَ لُوطٍ ۖ كَانَتَا تَحْتَ عَبْدَيْنِ مِنْ عِبَادِنَا صَالِحَيْنِ فَخَانَتَاهُمَا فَلَمْ يُغْنِيَا عَنْهُمَا مِنَ اللَّهِ شَيْئًا وَقِيلَ ادْخُلَا النَّارَ مَعَ الدَّاخِلِينَ', NULL, 9, 'quran_66');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_79_verse_3', 'quran79verse3', 'Quran 79:3', 'quran-79-verse-3', '(Original Book)', 'ara', 'وَالسَّابِحَاتِ سَبْحًا', NULL, 2, 'quran_79');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_66_verse_11', 'quran66verse11', 'Quran 66:11', 'quran-66-verse-11', '(Original Book)', 'ara', 'وَضَرَبَ اللَّهُ مَثَلًا لِلَّذِينَ آمَنُوا امْرَأَتَ فِرْعَوْنَ إِذْ قَالَتْ رَبِّ ابْنِ لِي عِنْدَكَ بَيْتًا فِي الْجَنَّةِ وَنَجِّنِي مِنْ فِرْعَوْنَ وَعَمَلِهِ وَنَجِّنِي مِنَ الْقَوْمِ الظَّالِمِينَ', NULL, 10, 'quran_66');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_66_verse_12', 'quran66verse12', 'Quran 66:12', 'quran-66-verse-12', '(Original Book)', 'ara', 'وَمَرْيَمَ ابْنَتَ عِمْرَانَ الَّتِي أَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيهِ مِنْ رُوحِنَا وَصَدَّقَتْ بِكَلِمَاتِ رَبِّهَا وَكُتُبِهِ وَكَانَتْ مِنَ الْقَانِتِينَ', NULL, 11, 'quran_66');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_1', 'quran67verse1', 'Quran 67:1', 'quran-67-verse-1', '(Original Book)', 'ara', 'بِسْمِ اللَّهِ الرَّحْمَٰنِ الرَّحِيمِ تَبَارَكَ الَّذِي بِيَدِهِ الْمُلْكُ وَهُوَ عَلَىٰ كُلِّ شَيْءٍ قَدِيرٌ', NULL, 0, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_2', 'quran67verse2', 'Quran 67:2', 'quran-67-verse-2', '(Original Book)', 'ara', 'الَّذِي خَلَقَ الْمَوْتَ وَالْحَيَاةَ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلًا ۚ وَهُوَ الْعَزِيزُ الْغَفُورُ', NULL, 1, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_3', 'quran67verse3', 'Quran 67:3', 'quran-67-verse-3', '(Original Book)', 'ara', 'الَّذِي خَلَقَ سَبْعَ سَمَاوَاتٍ طِبَاقًا ۖ مَا تَرَىٰ فِي خَلْقِ الرَّحْمَٰنِ مِنْ تَفَاوُتٍ ۖ فَارْجِعِ الْبَصَرَ هَلْ تَرَىٰ مِنْ فُطُورٍ', NULL, 2, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_4', 'quran67verse4', 'Quran 67:4', 'quran-67-verse-4', '(Original Book)', 'ara', 'ثُمَّ ارْجِعِ الْبَصَرَ كَرَّتَيْنِ يَنْقَلِبْ إِلَيْكَ الْبَصَرُ خَاسِئًا وَهُوَ حَسِيرٌ', NULL, 3, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_5', 'quran67verse5', 'Quran 67:5', 'quran-67-verse-5', '(Original Book)', 'ara', 'وَلَقَدْ زَيَّنَّا السَّمَاءَ الدُّنْيَا بِمَصَابِيحَ وَجَعَلْنَاهَا رُجُومًا لِلشَّيَاطِينِ ۖ وَأَعْتَدْنَا لَهُمْ عَذَابَ السَّعِيرِ', NULL, 4, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_6', 'quran67verse6', 'Quran 67:6', 'quran-67-verse-6', '(Original Book)', 'ara', 'وَلِلَّذِينَ كَفَرُوا بِرَبِّهِمْ عَذَابُ جَهَنَّمَ ۖ وَبِئْسَ الْمَصِيرُ', NULL, 5, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_7', 'quran67verse7', 'Quran 67:7', 'quran-67-verse-7', '(Original Book)', 'ara', 'إِذَا أُلْقُوا فِيهَا سَمِعُوا لَهَا شَهِيقًا وَهِيَ تَفُورُ', NULL, 6, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_8', 'quran67verse8', 'Quran 67:8', 'quran-67-verse-8', '(Original Book)', 'ara', 'تَكَادُ تَمَيَّزُ مِنَ الْغَيْظِ ۖ كُلَّمَا أُلْقِيَ فِيهَا فَوْجٌ سَأَلَهُمْ خَزَنَتُهَا أَلَمْ يَأْتِكُمْ نَذِيرٌ', NULL, 7, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_9', 'quran67verse9', 'Quran 67:9', 'quran-67-verse-9', '(Original Book)', 'ara', 'قَالُوا بَلَىٰ قَدْ جَاءَنَا نَذِيرٌ فَكَذَّبْنَا وَقُلْنَا مَا نَزَّلَ اللَّهُ مِنْ شَيْءٍ إِنْ أَنْتُمْ إِلَّا فِي ضَلَالٍ كَبِيرٍ', NULL, 8, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_10', 'quran67verse10', 'Quran 67:10', 'quran-67-verse-10', '(Original Book)', 'ara', 'وَقَالُوا لَوْ كُنَّا نَسْمَعُ أَوْ نَعْقِلُ مَا كُنَّا فِي أَصْحَابِ السَّعِيرِ', NULL, 9, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_11', 'quran67verse11', 'Quran 67:11', 'quran-67-verse-11', '(Original Book)', 'ara', 'فَاعْتَرَفُوا بِذَنْبِهِمْ فَسُحْقًا لِأَصْحَابِ السَّعِيرِ', NULL, 10, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_12', 'quran67verse12', 'Quran 67:12', 'quran-67-verse-12', '(Original Book)', 'ara', 'إِنَّ الَّذِينَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ لَهُمْ مَغْفِرَةٌ وَأَجْرٌ كَبِيرٌ', NULL, 11, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_13', 'quran67verse13', 'Quran 67:13', 'quran-67-verse-13', '(Original Book)', 'ara', 'وَأَسِرُّوا قَوْلَكُمْ أَوِ اجْهَرُوا بِهِ ۖ إِنَّهُ عَلِيمٌ بِذَاتِ الصُّدُورِ', NULL, 12, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_14', 'quran67verse14', 'Quran 67:14', 'quran-67-verse-14', '(Original Book)', 'ara', 'أَلَا يَعْلَمُ مَنْ خَلَقَ وَهُوَ اللَّطِيفُ الْخَبِيرُ', NULL, 13, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_15', 'quran67verse15', 'Quran 67:15', 'quran-67-verse-15', '(Original Book)', 'ara', 'هُوَ الَّذِي جَعَلَ لَكُمُ الْأَرْضَ ذَلُولًا فَامْشُوا فِي مَنَاكِبِهَا وَكُلُوا مِنْ رِزْقِهِ ۖ وَإِلَيْهِ النُّشُورُ', NULL, 14, 'quran_67');
INSERT INTO quranverse (id, canonicalslug, name, slug, author, inlanguage, text, textwithouttashkeel, versenum, chapter_id) VALUES ('quran_67_verse_16', 'quran67verse16', 'Quran 67:16', 'quran-67-verse-16', '(Original Book)', 'ara', 'أَأَمِنْتُمْ مَنْ فِي السَّمَاءِ أَنْ يَخْسِفَ بِكُمُ الْأَرْضَ فَإِذَا هِيَ تَمُورُ', NULL, 15, 'quran_67');
